contracts/P2PAnalytics.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./P2PMarketManager.sol";

contract MetricsHub is Ownable {
    
    // MarketManager reference
    address payable public marketManager;
    
    // User statistics
    struct UserStats {
        uint256 totalStakesPlaced;
        uint256 totalStakesWon;
        uint256 totalStakesLost;
        uint256 totalWinnings;
        uint256 totalLosses;
        uint256 totalSupportDonated;
        uint256 marketsCreated;
        uint256 marketsWon;
        uint256 marketsLost;
        uint256 favoriteOption; // Most staked on option
        uint256 lastActivity;
    }
    
    // Market statistics
    struct MarketStats {
        uint256 totalVolume;
        uint256 totalStakers;
        uint256 totalSupporters;
        uint256 averageStakeSize;
        uint256 largestStake;
        uint256 mostPopularOption;
        uint256 resolutionTime;
        bool wasResolved;
        uint256 creatorWinnings;
    }
    
    // Global statistics
    struct GlobalStats {
        uint256 totalMarkets;
        uint256 totalVolume;
        uint256 totalStakers;
        uint256 totalSupporters;
        uint256 totalWinnings;
        uint256 averageMarketSize;
        uint256 mostActiveToken;
        uint256 averageResolutionTime;
    }
    
    // Mappings
    mapping(address => UserStats) public userStats;
    mapping(uint256 => MarketStats) public marketStats;
    mapping(address => uint256[]) public userMarkets;
    mapping(address => uint256[]) public userStakes;
    mapping(address => uint256[]) public userSupports;
    mapping(address => mapping(uint256 => uint256)) public userOptionStats; // user => option => count
    
    // Events
    event UserStatsUpdated(address indexed user, uint256 totalStakes, uint256 winnings);
    event MarketStatsUpdated(uint256 indexed marketId, uint256 volume, uint256 stakers);
    event GlobalStatsUpdated(uint256 totalMarkets, uint256 totalVolume);
    
    constructor(address initialOwner, address _marketManager) Ownable(initialOwner) {
        marketManager = payable(_marketManager);
    }
    
    /**
     * @dev Update market manager address
     */
    function setMarketManager(address _marketManager) external onlyOwner {
        marketManager = payable(_marketManager);
    }
    
    /**
     * @dev Track stake placement
     */
    function trackStake(uint256 marketId, address user, uint256 option, uint256 amount) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        UserStats storage stats = userStats[user];
        stats.totalStakesPlaced++;
        stats.totalLosses += amount; // Will be updated when resolved
        stats.lastActivity = block.timestamp;
        
        // Track option preference
        userOptionStats[user][option]++;
        if (userOptionStats[user][option] > userOptionStats[user][stats.favoriteOption]) {
            stats.favoriteOption = option;
        }
        
        // Update market stats
        MarketStats storage marketStat = marketStats[marketId];
        marketStat.totalVolume += amount;
        marketStat.totalStakers = P2PMarketManager(marketManager).getStakerCount(marketId);
        marketStat.averageStakeSize = marketStat.totalStakers > 0 ? marketStat.totalVolume / marketStat.totalStakers : 0;
        
        if (amount > marketStat.largestStake) {
            marketStat.largestStake = amount;
        }
        
        // Track user's markets
        if (userMarkets[user].length == 0 || userMarkets[user][userMarkets[user].length - 1] != marketId) {
            userMarkets[user].push(marketId);
        }
        userStakes[user].push(marketId);
        
        emit UserStatsUpdated(user, stats.totalStakesPlaced, stats.totalWinnings);
        emit MarketStatsUpdated(marketId, marketStat.totalVolume, marketStat.totalStakers);
    }
    
    /**
     * @dev Track support donation
     */
    function trackSupport(uint256 marketId, address user, uint256 amount) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        UserStats storage stats = userStats[user];
        stats.totalSupportDonated += amount;
        stats.lastActivity = block.timestamp;
        
        // Update market stats
        MarketStats storage marketStat = marketStats[marketId];
        marketStat.totalSupporters = P2PMarketManager(marketManager).getSupporterCount(marketId);
        
        userSupports[user].push(marketId);
        
        emit UserStatsUpdated(user, stats.totalStakesPlaced, stats.totalWinnings);
    }
    
    /**
     * @dev Track market creation
     */
    function trackMarketCreation(uint256 marketId, address creator) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        UserStats storage stats = userStats[creator];
        stats.marketsCreated++;
        stats.lastActivity = block.timestamp;
        
        userMarkets[creator].push(marketId);
        
        emit UserStatsUpdated(creator, stats.totalStakesPlaced, stats.totalWinnings);
    }
    
    /**
     * @dev Track market resolution
     */
    function trackMarketResolution(uint256 marketId, uint256 winningOption) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        MarketStats storage marketStat = marketStats[marketId];
        marketStat.wasResolved = true;
        marketStat.resolutionTime = block.timestamp;
        marketStat.mostPopularOption = winningOption;
        
        // Update global stats (note: this is expensive, consider caching)
        // GlobalStats storage global = getGlobalStats();
        // global.totalMarkets++;
        // global.totalVolume += marketStat.totalVolume;
        
        emit GlobalStatsUpdated(0, 0); // Placeholder - global stats calculation is expensive
    }
    
    /**
     * @dev Track user winnings
     */
    function trackWinnings(uint256 marketId, address user, uint256 winnings) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        UserStats storage stats = userStats[user];
        stats.totalStakesWon++;
        stats.totalWinnings += winnings;
        stats.lastActivity = block.timestamp;
        
        // Check if user won the market they created
        P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(marketId);
        if (market.creator == user) {
            stats.marketsWon++;
            marketStats[marketId].creatorWinnings = winnings;
        }
        
        emit UserStatsUpdated(user, stats.totalStakesPlaced, stats.totalWinnings);
    }
    
    /**
     * @dev Track user loss
     */
    function trackLoss(uint256 marketId, address user, uint256 loss) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        UserStats storage stats = userStats[user];
        stats.totalStakesLost++;
        stats.lastActivity = block.timestamp;
        
        // Check if user lost the market they created
        P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(marketId);
        if (market.creator == user) {
            stats.marketsLost++;
        }
        
        emit UserStatsUpdated(user, stats.totalStakesPlaced, stats.totalWinnings);
    }
    
    /**
     * @dev Get user statistics
     */
    function getUserStats(address user) external view returns (UserStats memory) {
        return userStats[user];
    }
    
    /**
     * @dev Get market statistics
     */
    function getMarketStats(uint256 marketId) external view returns (MarketStats memory) {
        return marketStats[marketId];
    }
    
    /**
     * @dev Get global statistics
     */
    function getGlobalStats() public view returns (GlobalStats memory) {
        uint256 totalMarkets = P2PMarketManager(marketManager).getNextMarketId() - 1;
        uint256 totalVolume = 0;
        uint256 totalStakers = 0;
        uint256 totalSupporters = 0;
        
        // Calculate totals (this is expensive, consider caching)
        for (uint256 i = 1; i <= totalMarkets; i++) {
            MarketStats memory market = marketStats[i];
            totalVolume += market.totalVolume;
            totalStakers += market.totalStakers;
            totalSupporters += market.totalSupporters;
        }
        
        return GlobalStats({
            totalMarkets: totalMarkets,
            totalVolume: totalVolume,
            totalStakers: totalStakers,
            totalSupporters: totalSupporters,
            totalWinnings: 0, // Would need to track this
            averageMarketSize: totalMarkets > 0 ? totalVolume / totalMarkets : 0,
            mostActiveToken: 0, // Would need to track this
            averageResolutionTime: 0 // Would need to track this
        });
    }
    
    /**
     * @dev Get user's market history
     */
    function getUserMarkets(address user) external view returns (uint256[] memory) {
        return userMarkets[user];
    }
    
    /**
     * @dev Get user's stake history
     */
    function getUserStakes(address user) external view returns (uint256[] memory) {
        return userStakes[user];
    }
    
    /**
     * @dev Get user's support history
     */
    function getUserSupports(address user) external view returns (uint256[] memory) {
        return userSupports[user];
    }
    
    /**
     * @dev Get user's win rate
     */
    function getUserWinRate(address user) external view returns (uint256) {
        UserStats memory stats = userStats[user];
        if (stats.totalStakesPlaced == 0) return 0;
        return (stats.totalStakesWon * 100) / stats.totalStakesPlaced;
    }
    
    /**
     * @dev Get user's favorite option
     */
    function getUserFavoriteOption(address user) external view returns (uint256) {
        return userStats[user].favoriteOption;
    }
    
    /**
     * @dev Get markets filtered by creator
     */
    function getMarketsByCreator(address creator) external view returns (uint256[] memory) {
        return userMarkets[creator];
    }
    
    /**
     * @dev Get markets filtered by token
     */
    function getMarketsByToken(address token) external view returns (uint256[] memory) {
        uint256[] memory filteredMarkets = new uint256[](0);
        uint256 nextMarketId = P2PMarketManager(marketManager).getNextMarketId();
        
        for (uint256 i = 1; i < nextMarketId; i++) {
            P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(i);
            if (market.paymentToken == token) {
                // Add to filtered array (this is expensive, consider events)
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = i;
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get active markets filtered by minimum volume
     */
    function getActiveMarketsByMinVolume(uint256 minVolume) external view returns (uint256[] memory) {
        uint256[] memory filteredMarkets = new uint256[](0);
        uint256 nextMarketId = P2PMarketManager(marketManager).getNextMarketId();
        
        for (uint256 i = 1; i < nextMarketId; i++) {
            P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(i);
            if (market.state == P2PMarketManager.MarketState.Active && marketStats[i].totalVolume >= minVolume) {
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = i;
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get markets filtered by state
     */
    function getMarketsByState(P2PMarketManager.MarketState state) external view returns (uint256[] memory) {
        uint256[] memory filteredMarkets = new uint256[](0);
        uint256 nextMarketId = P2PMarketManager(marketManager).getNextMarketId();
        
        for (uint256 i = 1; i < nextMarketId; i++) {
            P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(i);
            if (market.state == state) {
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = i;
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get markets filtered by date range
     */
    function getMarketsByDateRange(uint256 startTime, uint256 endTime) external view returns (uint256[] memory) {
        uint256[] memory filteredMarkets = new uint256[](0);
        uint256 nextMarketId = P2PMarketManager(marketManager).getNextMarketId();
        
        for (uint256 i = 1; i < nextMarketId; i++) {
            P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(i);
            if (market.startTime >= startTime && market.startTime <= endTime) {
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = i;
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get markets filtered by multiple criteria
     */
    function getMarketsFiltered(
        address creator,
        address token,
        uint256 minVolume,
        uint256 maxVolume,
        P2PMarketManager.MarketState state,
        uint256 startTime,
        uint256 endTime
    ) external view returns (uint256[] memory) {
        uint256[] memory filteredMarkets = new uint256[](0);
        uint256 nextMarketId = P2PMarketManager(marketManager).getNextMarketId();
        
        for (uint256 i = 1; i < nextMarketId; i++) {
            P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(i);
            MarketStats memory stats = marketStats[i];
            
            bool matches = true;
            
            // Filter by creator
            if (creator != address(0) && market.creator != creator) {
                matches = false;
            }
            
            // Filter by token
            if (token != address(0) && market.paymentToken != token) {
                matches = false;
            }
            
            // Filter by volume range
            if (minVolume > 0 && stats.totalVolume < minVolume) {
                matches = false;
            }
            if (maxVolume > 0 && stats.totalVolume > maxVolume) {
                matches = false;
            }
            
            // Filter by state
            if (state != P2PMarketManager.MarketState.Active && market.state != state) {
                matches = false;
            }
            
            // Filter by date range
            if (startTime > 0 && market.startTime < startTime) {
                matches = false;
            }
            if (endTime > 0 && market.startTime > endTime) {
                matches = false;
            }
            
            if (matches) {
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = i;
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get user's markets filtered by state
     */
    function getUserMarketsByState(address user, P2PMarketManager.MarketState state) external view returns (uint256[] memory) {
        uint256[] memory userMarketsList = userMarkets[user];
        uint256[] memory filteredMarkets = new uint256[](0);
        
        for (uint256 i = 0; i < userMarketsList.length; i++) {
            P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(userMarketsList[i]);
            if (market.state == state) {
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = userMarketsList[i];
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get active markets sorted by volume (descending)
     */
    function getActiveMarketsByVolume(uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        uint256 nextMarketId = P2PMarketManager(marketManager).getNextMarketId();
        uint256[] memory marketIds = new uint256[](0);
        uint256[] memory volumes = new uint256[](0);
        
        // Collect only ACTIVE markets
        for (uint256 i = 1; i < nextMarketId; i++) {
            P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(i);
            if (market.state == P2PMarketManager.MarketState.Active) {
                // Add to arrays
                uint256[] memory tempIds = new uint256[](marketIds.length + 1);
                uint256[] memory tempVolumes = new uint256[](volumes.length + 1);
                
                for (uint256 j = 0; j < marketIds.length; j++) {
                    tempIds[j] = marketIds[j];
                    tempVolumes[j] = volumes[j];
                }
                tempIds[marketIds.length] = i;
                tempVolumes[volumes.length] = marketStats[i].totalVolume;
                
                marketIds = tempIds;
                volumes = tempVolumes;
            }
        }
        
        // Simple bubble sort (expensive on-chain, consider off-chain)
        for (uint256 i = 0; i < marketIds.length - 1; i++) {
            for (uint256 j = 0; j < marketIds.length - i - 1; j++) {
                if (volumes[j] < volumes[j + 1]) {
                    // Swap volumes
                    uint256 tempVolume = volumes[j];
                    volumes[j] = volumes[j + 1];
                    volumes[j + 1] = tempVolume;
                    
                    // Swap market IDs
                    uint256 tempId = marketIds[j];
                    marketIds[j] = marketIds[j + 1];
                    marketIds[j + 1] = tempId;
                }
            }
        }
        
        // Return limited results
        uint256 resultLength = limit > 0 && limit < marketIds.length ? limit : marketIds.length;
        uint256[] memory resultIds = new uint256[](resultLength);
        uint256[] memory resultVolumes = new uint256[](resultLength);
        
        for (uint256 i = 0; i < resultLength; i++) {
            resultIds[i] = marketIds[i];
            resultVolumes[i] = volumes[i];
        }
        
        return (resultIds, resultVolumes);
    }
    
    /**
     * @dev Get active markets by staker count (most stakers)
     */
    function getActiveMarketsByStakerCount(uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        uint256 nextMarketId = P2PMarketManager(marketManager).getNextMarketId();
        uint256[] memory marketIds = new uint256[](0);
        uint256[] memory stakerCounts = new uint256[](0);
        
        // Collect only ACTIVE markets
        for (uint256 i = 1; i < nextMarketId; i++) {
            P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(i);
            if (market.state == P2PMarketManager.MarketState.Active) {
                uint256 stakerCount = P2PMarketManager(marketManager).getStakerCount(i);
                
                // Add to arrays
                uint256[] memory tempIds = new uint256[](marketIds.length + 1);
                uint256[] memory tempCounts = new uint256[](stakerCounts.length + 1);
                
                for (uint256 j = 0; j < marketIds.length; j++) {
                    tempIds[j] = marketIds[j];
                    tempCounts[j] = stakerCounts[j];
                }
                tempIds[marketIds.length] = i;
                tempCounts[stakerCounts.length] = stakerCount;
                
                marketIds = tempIds;
                stakerCounts = tempCounts;
            }
        }
        
        // Sort by staker count (bubble sort)
        for (uint256 i = 0; i < marketIds.length - 1; i++) {
            for (uint256 j = 0; j < marketIds.length - i - 1; j++) {
                if (stakerCounts[j] < stakerCounts[j + 1]) {
                    // Swap counts
                    uint256 tempCount = stakerCounts[j];
                    stakerCounts[j] = stakerCounts[j + 1];
                    stakerCounts[j + 1] = tempCount;
                    
                    // Swap market IDs
                    uint256 tempId = marketIds[j];
                    marketIds[j] = marketIds[j + 1];
                    marketIds[j + 1] = tempId;
                }
            }
        }
        
        // Return limited results
        uint256 resultLength = limit > 0 && limit < marketIds.length ? limit : marketIds.length;
        uint256[] memory resultIds = new uint256[](resultLength);
        uint256[] memory resultCounts = new uint256[](resultLength);
        
        for (uint256 i = 0; i < resultLength; i++) {
            resultIds[i] = marketIds[i];
            resultCounts[i] = stakerCounts[i];
        }
        
        return (resultIds, resultCounts);
    }
    
    /**
     * @dev Get active markets by total participants (stakers + supporters)
     */
    function getActiveMarketsByParticipants(uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        uint256 nextMarketId = P2PMarketManager(marketManager).getNextMarketId();
        uint256[] memory marketIds = new uint256[](0);
        uint256[] memory participantCounts = new uint256[](0);
        
        // Collect only ACTIVE markets
        for (uint256 i = 1; i < nextMarketId; i++) {
            P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(i);
            if (market.state == P2PMarketManager.MarketState.Active) {
            uint256 stakerCount = P2PMarketManager(marketManager).getStakerCount(i);
            uint256 supporterCount = P2PMarketManager(marketManager).getSupporterCount(i);
            uint256 totalParticipants = stakerCount + supporterCount;
                
                // Add to arrays
                uint256[] memory tempIds = new uint256[](marketIds.length + 1);
                uint256[] memory tempCounts = new uint256[](participantCounts.length + 1);
                
                for (uint256 j = 0; j < marketIds.length; j++) {
                    tempIds[j] = marketIds[j];
                    tempCounts[j] = participantCounts[j];
                }
                tempIds[marketIds.length] = i;
                tempCounts[participantCounts.length] = totalParticipants;
                
                marketIds = tempIds;
                participantCounts = tempCounts;
            }
        }
        
        // Sort by total participants (bubble sort)
        for (uint256 i = 0; i < marketIds.length - 1; i++) {
            for (uint256 j = 0; j < marketIds.length - i - 1; j++) {
                if (participantCounts[j] < participantCounts[j + 1]) {
                    // Swap counts
                    uint256 tempCount = participantCounts[j];
                    participantCounts[j] = participantCounts[j + 1];
                    participantCounts[j + 1] = tempCount;
                    
                    // Swap market IDs
                    uint256 tempId = marketIds[j];
                    marketIds[j] = marketIds[j + 1];
                    marketIds[j + 1] = tempId;
                }
            }
        }
        
        // Return limited results
        uint256 resultLength = limit > 0 && limit < marketIds.length ? limit : marketIds.length;
        uint256[] memory resultIds = new uint256[](resultLength);
        uint256[] memory resultCounts = new uint256[](resultLength);
        
        for (uint256 i = 0; i < resultLength; i++) {
            resultIds[i] = marketIds[i];
            resultCounts[i] = participantCounts[i];
        }
        
        return (resultIds, resultCounts);
    }
    
    /**
     * @dev Get recent active markets (created within last N days)
     */
    function getRecentActiveMarkets(uint256 daysBack, uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        uint256 nextMarketId = P2PMarketManager(marketManager).getNextMarketId();
        uint256[] memory marketIds = new uint256[](0);
        uint256[] memory timestamps = new uint256[](0);
        
        uint256 cutoffTime = block.timestamp - (daysBack * 1 days);
        
        // Collect only ACTIVE markets created within the time period
        for (uint256 i = 1; i < nextMarketId; i++) {
            P2PMarketManager.Market memory market = P2PMarketManager(marketManager).getMarket(i);
            if (market.state == P2PMarketManager.MarketState.Active && market.startTime >= cutoffTime) {
                // Add to arrays
                uint256[] memory tempIds = new uint256[](marketIds.length + 1);
                uint256[] memory tempTimestamps = new uint256[](timestamps.length + 1);
                
                for (uint256 j = 0; j < marketIds.length; j++) {
                    tempIds[j] = marketIds[j];
                    tempTimestamps[j] = timestamps[j];
                }
                tempIds[marketIds.length] = i;
                tempTimestamps[timestamps.length] = market.startTime;
                
                marketIds = tempIds;
                timestamps = tempTimestamps;
            }
        }
        
        // Sort by creation time (newest first)
        for (uint256 i = 0; i < marketIds.length - 1; i++) {
            for (uint256 j = 0; j < marketIds.length - i - 1; j++) {
                if (timestamps[j] < timestamps[j + 1]) {
                    // Swap timestamps
                    uint256 tempTimestamp = timestamps[j];
                    timestamps[j] = timestamps[j + 1];
                    timestamps[j + 1] = tempTimestamp;
                    
                    // Swap market IDs
                    uint256 tempId = marketIds[j];
                    marketIds[j] = marketIds[j + 1];
                    marketIds[j + 1] = tempId;
                }
            }
        }
        
        // Return limited results
        uint256 resultLength = limit > 0 && limit < marketIds.length ? limit : marketIds.length;
        uint256[] memory resultIds = new uint256[](resultLength);
        uint256[] memory resultTimestamps = new uint256[](resultLength);
        
        for (uint256 i = 0; i < resultLength; i++) {
            resultIds[i] = marketIds[i];
            resultTimestamps[i] = timestamps[i];
        }
        
        return (resultIds, resultTimestamps);
    }
    
    /**
     * @dev Get active markets created today
     */
    function getTodayActiveMarkets(uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        return this.getRecentActiveMarkets(1, limit);
    }
    
    /**
     * @dev Get active markets created this week
     */
    function getThisWeekActiveMarkets(uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        return this.getRecentActiveMarkets(7, limit);
    }
    
    /**
     * @dev Get top stakers by volume
     */
    function getTopStakers(uint256 limit) external view returns (address[] memory, uint256[] memory) {
        // This would require sorting, which is expensive on-chain
        // Consider implementing off-chain or with events
        address[] memory empty;
        uint256[] memory empty2;
        return (empty, empty2);
    }
}

contracts/P2PMarketManager.sol.content:
// @p2p-oracle-system
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title EventPool
 * @notice P2P prediction market contract.
 *
 * ─── Oracle integration (P2POPTIMISTIC markets) ──────────────────────────────
 *
 *  Resolution flow:
 *
 *  1. endMarket(id)                  — anyone, after market.endTime
 *  2. requestP2PResolution(id, ...)  — anyone except creator, after endTime;
 *                                      posts a claim + bond to the oracle
 *  3. [optional] disputeOracle(id)   — anyone, after assertionDeadline and
 *                                      before expirationTime; puts up counter-bond
 *  4. settleOracle(id)               — anyone, after expirationTime
 *  5. resolveP2PMarket(id)           — anyone, after oracle is settled
 *
 *  Fallback paths that prevent permanent fund-lock:
 *
 *  • If nobody asserts within ASSERTION_GRACE_PERIOD after endTime
 *    → cancelMarketNoAssertion(id) cancels the market and allows refunds.
 *
 *  • If oracle settles with result=false (disputer won / assertion was wrong)
 *    → resolveP2PMarket() cancels the market and allows refunds.
 *      (The asserter lied; we don't know the real outcome, so refund everyone.)
 *
 *  • If oracle vote had no consensus, the oracle automatically accepts the
 *    assertion as true and returns both bonds. Market resolves normally.
 *    This path is handled inside the oracle itself (see P2POptimisticOracle.sol).
 */

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./IOptimisticOracle.sol";
import "./P2PTreasury.sol";
import "./P2PAnalytics.sol";

interface IAdminManager {
    function supportedTokens(address) external view returns (bool);
    function tokenSymbols(address) external view returns (string memory);
    function blacklistedWallets(address) external view returns (bool);
    function isMarketDeleted(uint256) external view returns (bool);
    function minMarketDurationMinutes() external view returns (uint256);
    function bettingRestrictionEnabled() external view returns (bool);
    function bettingRestrictionMinutes() external view returns (uint256);
    function analytics() external view returns (address);
}

interface AggregatorV3Interface {
    function latestRoundData()
        external
        view
        returns (uint80, int256, uint256, uint256, uint80);
    function decimals() external view returns (uint8);
}

contract P2PMarketManager is Ownable {
    using SafeERC20 for IERC20;

    // ─── Types ────────────────────────────────────────────────────────────────

    enum MarketState { Active, Ended, Resolved, Cancelled, Deleted }

    enum MarketType {
        PRICE_FEED,    // Resolves via on-chain Chainlink-compatible feed
        P2POPTIMISTIC  // Resolves via P2P Optimistic Oracle + voting
    }

    struct Market {
        address     creator;
        string      ipfsHash;
        bool        isMultiOption;
        uint256     maxOptions;
        address     paymentToken;
        uint256     minStake;
        uint256     creatorDeposit;
        uint256     creatorOutcome;
        uint256     startTime;
        uint256     stakeEndTime;
        uint256     endTime;
        uint256     resolutionEndTime;
        MarketState state;
        uint256     winningOption;
        bool        isResolved;
        MarketType  marketType;
        // PRICE_FEED fields
        address     priceFeed;
        uint256     priceThreshold;
        // P2POPTIMISTIC fields
        bytes32     p2pAssertionId;
        bool        p2pAssertionMade;
        uint256     p2pDisputedOptionId; // Option ID that the disputer claimed (0 if not disputed or no option specified)
    }

    // ─── Constants ────────────────────────────────────────────────────────────

    uint256 public constant marketCreationFee = 1e18;
    uint256 public constant CREATOR_FEE_BPS   = 350;  // 3.5 %
    uint256 public constant PLATFORM_FEE_BPS  = 500;  // 5.0 %

    /**
     * @notice Grace period after market.endTime during which someone must assert.
     *         If no assertion is made within this window, the market can be cancelled.
     *         Set to 48 hours to give asserters plenty of time.
     */
    uint256 public assertionGracePeriod = 48 hours;

    // ─── Storage ──────────────────────────────────────────────────────────────

    mapping(uint256 => Market)   public markets;
    mapping(uint256 => mapping(address => bool))    public userHasStaked;
    mapping(uint256 => mapping(address => bool))    public userHasSupported;
    mapping(uint256 => mapping(address => uint256)) public userStakeOptions;
    mapping(uint256 => address[]) public marketStakers;
    mapping(uint256 => address[]) public marketSupporters;
    mapping(address => uint256[]) public userMarketHistory;

    uint256   public nextMarketId = 1;
    uint256[] public activeMarkets;
    mapping(uint256 => bool) public isActiveMarket;

    address payable public treasury;
    address public adminManager;

    // Oracle config
    IOptimisticOracle public optimisticOracle;
    address           public defaultBondCurrency;

    // ─── Events ───────────────────────────────────────────────────────────────

    event MarketCreated(
        uint256 indexed marketId, address indexed creator, string ipfsHash,
        bool isMultiOption, uint256 maxOptions, address paymentToken,
        uint256 minStake, uint256 startTime, uint256 stakeEndTime,
        uint256 endTime, uint256 resolutionEndTime
    );
    event StakePlaced(uint256 indexed marketId, address indexed user, uint256 option, uint256 amount);
    event MarketResolved(uint256 indexed marketId, uint256 winningOption);
    event MarketCancelled(uint256 indexed marketId, string reason);
    event RefundClaimed(uint256 indexed marketId, address indexed user, uint256 amount);
    event WinningsClaimed(uint256 indexed marketId, address indexed user, uint256 amount);
    event ResolutionRequested(uint256 indexed marketId, bytes32 indexed assertionId);
    event OracleDisputed(uint256 indexed marketId, bytes32 indexed assertionId, address indexed disputer);
    event OracleSettled(uint256 indexed marketId, bytes32 indexed assertionId);
    event OracleResultProcessed(uint256 indexed marketId, bytes32 indexed assertionId, bool accepted);

    // ─── Constructor ──────────────────────────────────────────────────────────

    constructor(
        address        initialOwner,
        address payable _treasury,
        address        _adminManager
    ) Ownable(initialOwner) {
        treasury     = _treasury;
        adminManager = _adminManager;
    }

    // ─── Modifiers ────────────────────────────────────────────────────────────

    modifier notBlacklisted() {
        require(!IAdminManager(adminManager).blacklistedWallets(msg.sender), "EP: blacklisted");
        _;
    }

    modifier notDeleted(uint256 marketId) {
        require(!IAdminManager(adminManager).isMarketDeleted(marketId), "EP: deleted");
        _;
    }

    // ─── Market creation ──────────────────────────────────────────────────────

    /**
     * @notice Create a new prediction market.
     * @param marketType        0 = PRICE_FEED, 1 = P2POPTIMISTIC
     * @param priceFeed         Chainlink-compatible feed (PRICE_FEED only, else address(0))
     * @param priceThreshold    Price in feed units (PRICE_FEED only, else 0)
     */
    function createMarket(
        string memory ipfsHash,
        bool          isMultiOption,
        uint256       maxOptions,
        address       paymentToken,
        uint256       minStake,
        uint256       creatorDeposit,
        uint256       creatorOutcome,
        uint256       stakeDurationMinutes,
        uint256       resolutionDurationMinutes,
        MarketType    marketType,
        address       priceFeed,
        uint256       priceThreshold
    ) external payable notBlacklisted returns (uint256) {
        require(bytes(ipfsHash).length > 0,                     "EP: empty hash");
        require(maxOptions >= 2 && maxOptions <= 10,            "EP: bad options");
        require(minStake > 0,                                   "EP: bad stake");
        require(creatorDeposit > 0,                             "EP: bad deposit");
        require(creatorOutcome > 0 && creatorOutcome <= maxOptions, "EP: bad outcome");

        if (marketType == MarketType.PRICE_FEED) {
            require(priceFeed != address(0),  "EP: feed required");
            require(priceThreshold > 0,       "EP: threshold required");
        }

        uint256 minDuration = IAdminManager(adminManager).minMarketDurationMinutes();
        require(stakeDurationMinutes    >= minDuration,            "EP: min duration");
        require(resolutionDurationMinutes >= stakeDurationMinutes, "EP: resolution after stake");

        if (isMultiOption) {
            require(paymentToken != address(0), "EP: multi needs token");
            require(IAdminManager(adminManager).supportedTokens(paymentToken), "EP: unsupported token");
        } else if (paymentToken != address(0)) {
            require(IAdminManager(adminManager).supportedTokens(paymentToken), "EP: unsupported token");
        }

        require(msg.value >= marketCreationFee, "EP: fee too low");

        uint256 startTime     = block.timestamp;
        uint256 stakeEndTime  = startTime + stakeDurationMinutes    * 1 minutes;
        uint256 endTime       = startTime + resolutionDurationMinutes * 1 minutes;
        // For P2POPTIMISTIC: add assertionWindow(2h) + disputeWindow(12h) + buffer(2h) = 16h
        uint256 resolutionEndTime = marketType == MarketType.PRICE_FEED
            ? endTime
            : endTime + 16 hours;

        uint256 marketId = nextMarketId++;
        markets[marketId] = Market({
            creator:           msg.sender,
            ipfsHash:          ipfsHash,
            isMultiOption:     isMultiOption,
            maxOptions:        maxOptions,
            paymentToken:      paymentToken,
            minStake:          minStake,
            creatorDeposit:    creatorDeposit,
            creatorOutcome:    creatorOutcome,
            startTime:         startTime,
            stakeEndTime:      stakeEndTime,
            endTime:           endTime,
            resolutionEndTime: resolutionEndTime,
            state:             MarketState.Active,
            winningOption:     0,
            isResolved:        false,
            marketType:        marketType,
            priceFeed:         priceFeed,
            priceThreshold:    priceThreshold,
            p2pAssertionId:    bytes32(0),
            p2pAssertionMade:  false,
            p2pDisputedOptionId: 0
        });

        activeMarkets.push(marketId);
        isActiveMarket[marketId] = true;

        // Collect creation fee
        if (paymentToken == address(0)) {
            require(msg.value >= marketCreationFee + creatorDeposit, "EP: insufficient PEPU");
            (bool ok1, ) = owner().call{value: marketCreationFee}("");
            require(ok1, "EP: fee send failed");
            (bool ok2, ) = treasury.call{value: creatorDeposit}("");
            require(ok2, "EP: deposit send failed");
        } else {
            (bool ok1, ) = owner().call{value: marketCreationFee}("");
            require(ok1, "EP: fee send failed");
            IERC20(paymentToken).safeTransferFrom(msg.sender, treasury, creatorDeposit);
        }

        // Register creator's deposit as their stake
        userHasStaked[marketId][msg.sender]    = true;
        userStakeOptions[marketId][msg.sender] = creatorOutcome;
        userMarketHistory[msg.sender].push(marketId);

        PoolVault(treasury).placeStake(marketId, msg.sender, paymentToken, creatorDeposit, creatorOutcome);

        _trackCreation(marketId, msg.sender, creatorOutcome, creatorDeposit);

        emit MarketCreated(
            marketId, msg.sender, ipfsHash, isMultiOption, maxOptions,
            paymentToken, minStake, startTime, stakeEndTime, endTime, resolutionEndTime
        );
        return marketId;
    }

    // ─── Staking ──────────────────────────────────────────────────────────────

    /// @notice Stake PEPU (native token) on a market option.
    function placeStake(uint256 marketId, uint256 option)
        external payable notDeleted(marketId)
    {
        Market storage m = markets[marketId];
        require(m.state == MarketState.Active,       "EP: not active");
        require(m.paymentToken == address(0),        "EP: use placeStakeWithToken");
        require(option > 0 && option <= m.maxOptions, "EP: bad option");

        _checkBettingRestriction(m);

        uint256 amount = msg.value;
        require(amount >= m.minStake, "EP: below min");

        (bool ok, ) = treasury.call{value: amount}("");
        require(ok, "EP: treasury send failed");

        _recordStake(marketId, msg.sender, m.paymentToken, amount, option);
        emit StakePlaced(marketId, msg.sender, option, amount);
    }

    /// @notice Stake ERC-20 tokens on a market option.
    function placeStakeWithToken(uint256 marketId, uint256 option, uint256 amount)
        external notDeleted(marketId)
    {
        Market storage m = markets[marketId];
        require(m.state == MarketState.Active,       "EP: not active");
        require(m.paymentToken != address(0),        "EP: use placeStake");
        require(option > 0 && option <= m.maxOptions, "EP: bad option");
        require(amount >= m.minStake,                 "EP: below min");

        _checkBettingRestriction(m);

        IERC20(m.paymentToken).safeTransferFrom(msg.sender, treasury, amount);

        _recordStake(marketId, msg.sender, m.paymentToken, amount, option);
        emit StakePlaced(marketId, msg.sender, option, amount);
    }

    /// @notice Support a market with liquidity (no option chosen).
    function supportMarket(uint256 marketId, uint256 amount)
        external payable notBlacklisted notDeleted(marketId)
    {
        Market storage m = markets[marketId];
        require(m.state == MarketState.Active,    "EP: not active");
        require(block.timestamp < m.stakeEndTime, "EP: support closed");
        require(amount > 0,                       "EP: zero amount");

        if (m.paymentToken == address(0)) {
            require(msg.value == amount, "EP: ETH mismatch");
            (bool ok, ) = treasury.call{value: amount}("");
            require(ok, "EP: treasury send failed");
        } else {
            require(msg.value == 0, "EP: no ETH for token market");
            IERC20(m.paymentToken).safeTransferFrom(msg.sender, treasury, amount);
        }

        if (!userHasSupported[marketId][msg.sender]) {
            marketSupporters[marketId].push(msg.sender);
        }
        userHasSupported[marketId][msg.sender] = true;

        PoolVault(treasury).supportMarket(marketId, msg.sender, m.paymentToken, amount);

        address analytics = IAdminManager(adminManager).analytics();
        if (analytics != address(0)) MetricsHub(analytics).trackSupport(marketId, msg.sender, amount);
    }

    /// @notice Withdraw market support (before stakeEndTime - 12h, or if cancelled/deleted).
    function withdrawSupport(uint256 marketId) external {
        Market storage m = markets[marketId];
        require(userHasSupported[marketId][msg.sender], "EP: no support");

        if (m.state == MarketState.Active) {
            require(
                block.timestamp < m.stakeEndTime - 12 hours,
                "EP: too late to withdraw"
            );
        } else if (m.state == MarketState.Deleted || m.state == MarketState.Cancelled) {
            // always allowed
        } else {
            revert("EP: cannot withdraw now");
        }

        uint256 amt = PoolVault(treasury).getUserSupport(marketId, msg.sender, m.paymentToken);
        require(amt > 0, "EP: nothing to withdraw");

        PoolVault(treasury).withdrawSupport(marketId, msg.sender, m.paymentToken, amt);
        userHasSupported[marketId][msg.sender] = false;
    }

    // ─── Market lifecycle ─────────────────────────────────────────────────────

    /// @notice End the betting period. Anyone can call after endTime.
    function endMarket(uint256 marketId) external {
        Market storage m = markets[marketId];
        require(m.state == MarketState.Active,      "EP: not active");
        require(block.timestamp >= m.endTime,        "EP: not ended");

        m.state = MarketState.Ended;
        _removeFromActiveMarkets(marketId);
    }

    /// @notice Creator cancels (more than 12 h before stakeEndTime).
    function creatorCancelMarket(uint256 marketId) external {
        Market storage m = markets[marketId];
        require(msg.sender == m.creator,                          "EP: not creator");
        require(m.state == MarketState.Active,                    "EP: not active");
        require(block.timestamp < m.stakeEndTime - 12 hours,     "EP: too late");

        _cancelMarket(marketId, "Creator cancelled");
    }

    // ─── PRICE_FEED resolution ────────────────────────────────────────────────

    /// @notice Resolve a PRICE_FEED market by reading the on-chain oracle price.
    function resolvePriceFeedMarket(uint256 marketId) external {
        Market storage m = markets[marketId];
        require(m.marketType == MarketType.PRICE_FEED,  "EP: not PRICE_FEED");
        require(m.state == MarketState.Ended,            "EP: not ended");
        require(!m.isResolved,                           "EP: already resolved");
        require(block.timestamp >= m.resolutionEndTime,  "EP: too early");

        AggregatorV3Interface feed = AggregatorV3Interface(m.priceFeed);
        (, int256 price, , , ) = feed.latestRoundData();

        m.winningOption = uint256(price) >= m.priceThreshold ? 1 : 2;
        m.isResolved    = true;
        m.state         = MarketState.Resolved;

        _distributeFees(marketId);
        _trackResolution(marketId, m.winningOption);
        emit MarketResolved(marketId, m.winningOption);
    }

    // ─── P2POPTIMISTIC resolution ─────────────────────────────────────────────

    /**
     * @notice Step 1 — Post an assertion to the oracle claiming a specific option won.
     * @param marketId  The market to assert for.
     * @param optionId  The winning option (1-based). Encoded as callbackData.
     * @param claim     Human-readable UTF-8 description, e.g. "Option 2 (No) won".
     *
     * @dev Caller must have approved `defaultBondCurrency` for `minimumBond` to this
     *      contract before calling. This contract then approves the oracle and asserts.
     */
    function requestP2PResolution(
        uint256 marketId,
        uint256 optionId,
        bytes calldata claim
    ) external {
        Market storage m = markets[marketId];
        require(m.marketType == MarketType.P2POPTIMISTIC, "EP: not P2POPTIMISTIC");
        require(m.state == MarketState.Ended,              "EP: not ended");
        require(!m.isResolved,                             "EP: resolved");
        require(!m.p2pAssertionMade,                       "EP: assertion exists");
        require(msg.sender != m.creator,                   "EP: creator cannot assert");
        require(optionId > 0 && optionId <= m.maxOptions,  "EP: bad option");
        require(address(optimisticOracle) != address(0),   "EP: oracle not set");
        require(defaultBondCurrency != address(0),         "EP: bond currency not set");

        uint256 bond = optimisticOracle.getMinimumBond(defaultBondCurrency);

        // Pull bond from asserter to this contract, then approve oracle
        IERC20(defaultBondCurrency).safeTransferFrom(msg.sender, address(this), bond);
        IERC20(defaultBondCurrency).forceApprove(address(optimisticOracle), bond);

        // callbackData encodes (marketId, optionId) — oracle stores and returns it
        bytes memory callbackData = abi.encode(marketId, optionId);

        bytes32 assertionId = optimisticOracle.assertTruth(claim, msg.sender, callbackData);

        m.p2pAssertionId   = assertionId;
        m.p2pAssertionMade = true;

        emit ResolutionRequested(marketId, assertionId);
    }

    /**
     * @notice Step 2 (optional) — Dispute the oracle assertion.
     *         Can be called by anyone who disagrees, within the dispute window.
     *
     * @dev Caller must have approved `defaultBondCurrency` for `minimumBond` to this contract.
     * @param marketId The market to dispute.
     * @param optionId The option ID that the disputer claims is the correct outcome.
     */
    function disputeOracle(uint256 marketId, uint256 optionId) external {
        Market storage m = markets[marketId];
        require(m.marketType == MarketType.P2POPTIMISTIC, "EP: not P2POPTIMISTIC");
        require(m.p2pAssertionMade,                        "EP: no assertion");
        require(!m.isResolved,                             "EP: resolved");
        require(address(optimisticOracle) != address(0),   "EP: oracle not set");
        require(defaultBondCurrency != address(0),         "EP: bond currency not set");
        require(optionId > 0 && optionId <= m.maxOptions, "EP: invalid option");

        uint256 bond = optimisticOracle.getMinimumBond(defaultBondCurrency);

        IERC20(defaultBondCurrency).safeTransferFrom(msg.sender, address(this), bond);
        IERC20(defaultBondCurrency).forceApprove(address(optimisticOracle), bond);

        optimisticOracle.disputeAssertion(m.p2pAssertionId, msg.sender);

        // Store the disputed option so we can resolve to it if dispute wins
        m.p2pDisputedOptionId = optionId;

        emit OracleDisputed(marketId, m.p2pAssertionId, msg.sender);
    }

    /**
     * @notice Step 3 — Settle the oracle assertion after expiration.
     *         Anyone can call. Triggers vote resolution inside the oracle if needed.
     */
    function settleOracle(uint256 marketId) external {
        Market storage m = markets[marketId];
        require(m.marketType == MarketType.P2POPTIMISTIC, "EP: not P2POPTIMISTIC");
        require(m.p2pAssertionMade,                        "EP: no assertion");

        optimisticOracle.settleAssertion(m.p2pAssertionId);
        emit OracleSettled(marketId, m.p2pAssertionId);
    }

    /**
     * @notice Step 4 — Resolve the market using the settled oracle result.
     *
     *  Two outcomes:
     *    • result = true  → assertion was accepted; market resolves with the asserted optionId.
     *    • result = false → assertion was rejected (disputer won); market is cancelled
     *                       so all stakers can claim refunds. The true outcome is unknown.
     */
    function resolveP2PMarket(uint256 marketId) external {
        Market storage m = markets[marketId];
        require(m.marketType == MarketType.P2POPTIMISTIC, "EP: not P2POPTIMISTIC");
        require(m.p2pAssertionMade,                        "EP: no assertion");
        require(!m.isResolved,                             "EP: resolved");

        (bool oracleResult, bytes memory callbackData) =
            optimisticOracle.getAssertionResult(m.p2pAssertionId);

        emit OracleResultProcessed(marketId, m.p2pAssertionId, oracleResult);

        if (oracleResult) {
            // Assertion accepted — decode the winning option from callbackData
            (, uint256 optionId) = abi.decode(callbackData, (uint256, uint256));

            require(optionId > 0 && optionId <= m.maxOptions, "EP: bad option from oracle");

            m.winningOption = optionId;
            m.isResolved    = true;
            m.state         = MarketState.Resolved;

            _distributeFees(marketId);
            _trackResolution(marketId, m.winningOption);
            emit MarketResolved(marketId, m.winningOption);

        } else {
            // Assertion rejected — dispute won. Resolve to the disputed option.
            require(m.p2pDisputedOptionId > 0 && m.p2pDisputedOptionId <= m.maxOptions, "EP: no disputed option");

            m.winningOption = m.p2pDisputedOptionId;
            m.isResolved    = true;
            m.state         = MarketState.Resolved;

            _distributeFees(marketId);
            _trackResolution(marketId, m.winningOption);
            emit MarketResolved(marketId, m.winningOption);
        }
    }

    /**
     * @notice Cancel a P2POPTIMISTIC market if nobody made an assertion within the
     *         grace period after endTime. Allows stakers to claim full refunds.
     *
     *         This prevents permanent fund-lock when the market ends but no one asserts.
     */
    function cancelMarketNoAssertion(uint256 marketId) external {
        Market storage m = markets[marketId];
        require(m.marketType == MarketType.P2POPTIMISTIC,         "EP: not P2POPTIMISTIC");
        require(m.state == MarketState.Ended,                      "EP: not ended");
        require(!m.p2pAssertionMade,                               "EP: assertion exists");
        require(block.timestamp >= m.endTime + assertionGracePeriod, "EP: grace period active");

        _cancelMarket(marketId, unicode"No assertion made — refunds available");
    }

    /**
     * @notice Cancel a market after the full resolution period expires without resolution.
     *         Works for both market types. Serves as the last-resort escape hatch.
     */
    function cancelMarket(uint256 marketId) external {
        Market storage m = markets[marketId];
        require(m.state == MarketState.Ended,                  "EP: not ended");
        require(block.timestamp >= m.resolutionEndTime,         "EP: resolution period active");
        require(!m.isResolved,                                  "EP: already resolved");

        _cancelMarket(marketId, "Resolution period expired");
    }

    // ─── Claiming ─────────────────────────────────────────────────────────────

    function claimRefund(uint256 marketId) external {
        Market storage m = markets[marketId];
        require(m.state == MarketState.Cancelled, "EP: not cancelled");
        require(
            userHasStaked[marketId][msg.sender] || userHasSupported[marketId][msg.sender],
            "EP: nothing to refund"
        );

        uint256 refund;

        if (userHasStaked[marketId][msg.sender]) {
            refund += PoolVault(treasury).getUserStake(marketId, msg.sender, m.paymentToken);
            userHasStaked[marketId][msg.sender] = false;
        }

        if (userHasSupported[marketId][msg.sender]) {
            refund += PoolVault(treasury).getUserSupport(marketId, msg.sender, m.paymentToken);
            userHasSupported[marketId][msg.sender] = false;
        }

        require(refund > 0, "EP: zero refund");

        PoolVault(treasury).claimRefund(marketId, msg.sender, m.paymentToken, refund);
        emit RefundClaimed(marketId, msg.sender, refund);
    }

    function claimWinnings(uint256 marketId) external {
        Market storage m = markets[marketId];
        require(m.state == MarketState.Resolved,                      "EP: not resolved");
        require(m.winningOption > 0,                                   "EP: no winner");
        require(userHasStaked[marketId][msg.sender],                   "EP: no stake");
        require(!PoolVault(treasury).hasUserClaimed(marketId, msg.sender), "EP: claimed");

        uint256 winnings = _calculateWinnings(marketId, msg.sender);

        PoolVault(treasury).claimWinnings(marketId, msg.sender, m.paymentToken, winnings);
        emit WinningsClaimed(marketId, msg.sender, winnings);
    }

    function calculateWinnings(uint256 marketId, address user) external view returns (uint256) {
        Market storage m = markets[marketId];
        require(m.state == MarketState.Resolved,                    "EP: not resolved");
        require(m.winningOption > 0,                                 "EP: no winner");
        require(userHasStaked[marketId][user],                       "EP: no stake");
        require(!PoolVault(treasury).hasUserClaimed(marketId, user), "EP: claimed");
        return _calculateWinnings(marketId, user);
    }

    // ─── Internal helpers ─────────────────────────────────────────────────────

    function _calculateWinnings(uint256 marketId, address user) internal view returns (uint256) {
        Market storage m          = markets[marketId];
        uint256 userStake         = PoolVault(treasury).getUserStake(marketId, user, m.paymentToken);
        uint256 totalWinningStake = PoolVault(treasury).getOptionPool(marketId, m.winningOption, m.paymentToken);

        uint256 totalLosingPool;
        for (uint256 i = 1; i <= m.maxOptions; i++) {
            if (i != m.winningOption)
                totalLosingPool += PoolVault(treasury).getOptionPool(marketId, i, m.paymentToken);
        }
        totalLosingPool += PoolVault(treasury).getSupportPool(marketId, m.paymentToken);

        if (totalWinningStake == 0) return 0;

        bool isWinner = userStakeOptions[marketId][user] == m.winningOption;
        if (!isWinner) return 0;

        if (totalLosingPool == 0) return userStake; // all-win: everyone gets stake back

        uint256 winningsPool = (totalLosingPool * 9_000) / 10_000; // 90% after 10% fees
        return userStake + (userStake * winningsPool) / totalWinningStake;
    }

    function _distributeFees(uint256 marketId) internal {
        Market storage m = markets[marketId];

        uint256 totalLosingPool;
        for (uint256 i = 1; i <= m.maxOptions; i++) {
            if (i != m.winningOption)
                totalLosingPool += PoolVault(treasury).getOptionPool(marketId, i, m.paymentToken);
        }
        totalLosingPool += PoolVault(treasury).getSupportPool(marketId, m.paymentToken);

        uint256 totalWinningStake = PoolVault(treasury).getOptionPool(marketId, m.winningOption, m.paymentToken);
        uint256 totalPool         = PoolVault(treasury).getMarketPool(marketId, m.paymentToken);

        uint256 creatorFee  = (totalPool * CREATOR_FEE_BPS)  / 10_000;
        uint256 platformFee = (totalPool * PLATFORM_FEE_BPS) / 10_000;

        if (totalWinningStake == 0) {
            // All-lose: platform keeps everything after creator fee
            if (creatorFee > 0)
                PoolVault(treasury).transferToCreator(m.creator, m.paymentToken, creatorFee);
            uint256 platformTotal = totalPool - creatorFee;
            if (platformTotal > 0)
                PoolVault(treasury).addToPlatformPool(m.paymentToken, platformTotal);
        } else if (totalLosingPool == 0) {
            // All-win: no fees taken
        } else {
            if (creatorFee  > 0) PoolVault(treasury).transferToCreator(m.creator, m.paymentToken, creatorFee);
            if (platformFee > 0) PoolVault(treasury).distributeFees(m.paymentToken, platformFee);
        }
    }

    function _cancelMarket(uint256 marketId, string memory reason) internal {
        markets[marketId].state = MarketState.Cancelled;
        if (isActiveMarket[marketId]) _removeFromActiveMarkets(marketId);
        emit MarketCancelled(marketId, reason);
    }

    function _recordStake(
        uint256 marketId,
        address user,
        address token,
        uint256 amount,
        uint256 option
    ) internal {
        if (!userHasStaked[marketId][user]) {
            marketStakers[marketId].push(user);
            userMarketHistory[user].push(marketId);
        }
        userHasStaked[marketId][user]    = true;
        userStakeOptions[marketId][user] = option;

        PoolVault(treasury).placeStake(marketId, user, token, amount, option);

        address analytics = IAdminManager(adminManager).analytics();
        if (analytics != address(0)) MetricsHub(analytics).trackStake(marketId, user, option, amount);
    }

    function _checkBettingRestriction(Market storage m) internal view {
        if (IAdminManager(adminManager).bettingRestrictionEnabled()) {
            uint256 mins = IAdminManager(adminManager).bettingRestrictionMinutes();
            require(block.timestamp < m.stakeEndTime - mins * 1 minutes, "EP: staking closed");
        } else {
            require(block.timestamp < m.stakeEndTime, "EP: staking closed");
        }
    }

    function _trackCreation(uint256 marketId, address creator, uint256 option, uint256 amount) internal {
        address analytics = IAdminManager(adminManager).analytics();
        if (analytics != address(0)) {
            MetricsHub(analytics).trackMarketCreation(marketId, creator);
            MetricsHub(analytics).trackStake(marketId, creator, option, amount);
        }
    }

    function _trackResolution(uint256 marketId, uint256 winningOption) internal {
        address analytics = IAdminManager(adminManager).analytics();
        if (analytics != address(0)) MetricsHub(analytics).trackMarketResolution(marketId, winningOption);
    }

    function _removeFromActiveMarkets(uint256 marketId) internal {
        for (uint256 i = 0; i < activeMarkets.length; i++) {
            if (activeMarkets[i] == marketId) {
                activeMarkets[i] = activeMarkets[activeMarkets.length - 1];
                activeMarkets.pop();
                isActiveMarket[marketId] = false;
                break;
            }
        }
    }

    // ─── Admin ────────────────────────────────────────────────────────────────

    function setOptimisticOracle(address _oo) external onlyOwner {
        require(_oo != address(0), "EP: zero address");
        optimisticOracle = IOptimisticOracle(_oo);
    }

    function setDefaultBondCurrency(address _currency) external onlyOwner {
        require(_currency != address(0), "EP: zero address");
        defaultBondCurrency = _currency;
    }

    function setAssertionGracePeriod(uint256 _seconds) external onlyOwner {
        require(_seconds >= 1 hours, "EP: too short");
        assertionGracePeriod = _seconds;
    }

    function setAdminManager(address _adminManager) external onlyOwner {
        require(_adminManager != address(0), "EP: zero address");
        adminManager = _adminManager;
    }

    function markMarketDeleted(uint256 marketId) external {
        require(msg.sender == adminManager,              "EP: only AdminManager");
        require(marketId < nextMarketId,                 "EP: bad ID");
        require(markets[marketId].creator != address(0), "EP: no market");
        require(markets[marketId].state != MarketState.Deleted, "EP: already deleted");

        markets[marketId].state = MarketState.Deleted;
        if (isActiveMarket[marketId]) _removeFromActiveMarkets(marketId);
        emit MarketCancelled(marketId, "Deleted by admin");
    }

    function markMarketPermanentlyRemoved(uint256 marketId) external {
        require(msg.sender == adminManager,                        "EP: only AdminManager");
        require(markets[marketId].state == MarketState.Resolved,   "EP: not resolved");
        delete markets[marketId];
        if (isActiveMarket[marketId]) _removeFromActiveMarkets(marketId);
        emit MarketCancelled(marketId, "Permanently removed");
    }

    // ─── View functions ───────────────────────────────────────────────────────

    function getMarket(uint256 marketId) external view returns (Market memory) {
        return markets[marketId];
    }

    function getUserStake(uint256 marketId, address user, address token) external view returns (uint256) {
        return PoolVault(treasury).getUserStake(marketId, user, token);
    }

    function getOptionPool(uint256 marketId, uint256 option, address token) external view returns (uint256) {
        return PoolVault(treasury).getOptionPool(marketId, option, token);
    }

    function getTotalPool(uint256 marketId, address token) external view returns (uint256) {
        return PoolVault(treasury).getMarketPool(marketId, token);
    }

    function getSupportPool(uint256 marketId, address token) external view returns (uint256) {
        return PoolVault(treasury).getSupportPool(marketId, token);
    }

    function getUserSupport(uint256 marketId, address user, address token) external view returns (uint256) {
        return PoolVault(treasury).getUserSupport(marketId, user, token);
    }

    function getSupporterCount(uint256 marketId) external view returns (uint256) {
        return marketSupporters[marketId].length;
    }

    function getStakerCount(uint256 marketId) external view returns (uint256) {
        return marketStakers[marketId].length;
    }

    function getMarketStakers(uint256 marketId) external view returns (address[] memory) {
        return marketStakers[marketId];
    }

    function getNextMarketId() external view returns (uint256) {
        return nextMarketId;
    }

    receive() external payable {}
}

contracts/P2PTreasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

// ✅ COPY THIS FILE - Fixed PoolVault with platform pool tracking and proper partner allocation

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract PoolVault is Ownable {
    using SafeERC20 for IERC20;

    // Platform fee percentage (5% = 500 basis points)
    uint256 public constant PLATFORM_FEE_BPS = 500;
    
    // Partner fee percentage (20% of platform fee = 1% of total = 100 basis points)
    uint256 public constant PARTNER_FEE_BPS = 100;
    
    // Partner address
    address public partner;
    
    // Authorized contracts that can distribute fees
    mapping(address => bool) public authorizedContracts;
    
    // MarketManager address (can be changed)
    address public marketManager;
    
    // Platform pool tracking - separate for each token
    mapping(address => uint256) public platformPools; // token => platform pool amount
    
    // Market fund tracking
    mapping(uint256 => mapping(address => uint256)) public marketPools; // marketId => token => total amount
    mapping(uint256 => mapping(uint256 => mapping(address => uint256))) public optionPools; // marketId => option => token => amount
    mapping(uint256 => mapping(address => uint256)) public supportPools; // marketId => token => support amount
    mapping(uint256 => mapping(address => mapping(address => uint256))) public userStakes; // marketId => user => token => stake amount
    mapping(uint256 => mapping(address => mapping(address => uint256))) public userSupports; // marketId => user => token => support amount
    mapping(uint256 => mapping(address => mapping(uint256 => uint256))) public userBetOptions; // marketId => user => option => bet amount
    mapping(uint256 => mapping(address => bool)) public userClaimed; // marketId => user => has claimed
    mapping(uint256 => mapping(address => bool)) public userSupportClaimed; // marketId => user => has claimed support
    
    // Events
    event PartnerUpdated(address indexed oldPartner, address indexed newPartner);
    event FeesDistributed(address indexed token, uint256 platformAmount, uint256 partnerAmount);
    event PlatformPoolUpdated(address indexed token, uint256 amount, string reason);
    event PlatformFundsWithdrawn(address indexed token, uint256 amount, address indexed to);
    event FundsWithdrawn(address indexed token, uint256 amount, address indexed to);
    event ContractAuthorized(address indexed contractAddr);
    event ContractDeauthorized(address indexed contractAddr);
    event MarketManagerUpdated(address indexed oldManager, address indexed newManager);

    constructor(address initialOwner, address _partner) Ownable(initialOwner) {
        partner = _partner;
    }

    /**
     * @dev Update partner address
     */
    function setPartner(address _partner) external onlyOwner {
        require(_partner != address(0), "Treasury: Invalid partner address");
        address oldPartner = partner;
        partner = _partner;
        emit PartnerUpdated(oldPartner, _partner);
    }

    /**
     * @dev Calculate platform and partner fees from total amount
     */
    function calculateFees(uint256 totalAmount) public pure returns (uint256 platformFee, uint256 partnerFee) {
        platformFee = (totalAmount * PLATFORM_FEE_BPS) / 10000;
        partnerFee = (platformFee * PARTNER_FEE_BPS) / 10000;
    }

    /**
     * @dev Authorize a contract to distribute fees
     */
    function authorizeContract(address contractAddr) external onlyOwner {
        require(contractAddr != address(0), "Treasury: Invalid contract address");
        authorizedContracts[contractAddr] = true;
        emit ContractAuthorized(contractAddr);
    }

    /**
     * @dev Deauthorize a contract from distributing fees
     */
    function deauthorizeContract(address contractAddr) external onlyOwner {
        authorizedContracts[contractAddr] = false;
        emit ContractDeauthorized(contractAddr);
    }

    /**
     * @dev Set MarketManager address
     */
    function setMarketManager(address _marketManager) external onlyOwner {
        require(_marketManager != address(0), "Treasury: Invalid market manager address");
        address oldManager = marketManager;
        marketManager = _marketManager;
        
        // Auto-authorize the new market manager
        authorizedContracts[_marketManager] = true;
        
        emit MarketManagerUpdated(oldManager, _marketManager);
        emit ContractAuthorized(_marketManager);
    }

    /**
     * @dev Distribute fees to platform and partner (for normal fee distribution)
     * @param token ERC20 token address (address(0) for native ETH)
     * @param totalAmount Total amount to distribute fees from
     */
    function distributeFees(address token, uint256 totalAmount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can distribute fees");
        require(totalAmount > 0, "Treasury: Invalid amount");
        
        (uint256 platformFee, uint256 partnerFee) = calculateFees(totalAmount);
        
        if (token == address(0)) {
            // Native ETH
            require(address(this).balance >= totalAmount, "Treasury: Insufficient ETH balance");
            
            if (partnerFee > 0) {
                (bool success, ) = partner.call{value: partnerFee}("");
                require(success, "Treasury: ETH transfer to partner failed");
            }
            
            // Add platform fee to platform pool
            platformPools[token] += (platformFee - partnerFee);
            emit PlatformPoolUpdated(token, (platformFee - partnerFee), "Regular fee distribution");
            
            emit FeesDistributed(token, platformFee, partnerFee);
        } else {
            // ERC20 token
            IERC20 tokenContract = IERC20(token);
            require(tokenContract.balanceOf(address(this)) >= totalAmount, "Treasury: Insufficient token balance");
            
            if (partnerFee > 0) {
                tokenContract.safeTransfer(partner, partnerFee);
            }
            
            // Add platform fee to platform pool
            platformPools[token] += (platformFee - partnerFee);
            emit PlatformPoolUpdated(token, (platformFee - partnerFee), "Regular fee distribution");
            
            emit FeesDistributed(token, platformFee, partnerFee);
        }
    }

    /**
     * @dev Add funds directly to platform pool (for "everyone loses" scenarios)
     * @param token ERC20 token address (address(0) for native ETH)
     * @param amount Amount to add to platform pool
     */
    function addToPlatformPool(address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can add to platform pool");
        require(amount > 0, "Treasury: Invalid amount");
        
        // Partner gets 20% of the platform amount (same as normal fee distribution)
        uint256 partnerFee = (amount * 2000) / 10000; // 20% of platform amount
        uint256 platformAmount = amount - partnerFee;
        
        if (token == address(0)) {
            // Native ETH
            require(address(this).balance >= amount, "Treasury: Insufficient ETH balance");
            
            if (partnerFee > 0) {
                (bool success, ) = partner.call{value: partnerFee}("");
                require(success, "Treasury: ETH transfer to partner failed");
            }
        } else {
            // ERC20 token
            IERC20 tokenContract = IERC20(token);
            require(tokenContract.balanceOf(address(this)) >= amount, "Treasury: Insufficient token balance");
            
            if (partnerFee > 0) {
                tokenContract.safeTransfer(partner, partnerFee);
            }
        }
        
        // Add remaining amount to platform pool
        platformPools[token] += platformAmount;
        
        emit PlatformPoolUpdated(token, platformAmount, "Direct platform pool addition");
        emit FeesDistributed(token, platformAmount, partnerFee);
    }

    /**
     * @dev Get platform pool balance for a specific token
     * @param token ERC20 token address (address(0) for native ETH)
     */
    function getPlatformPool(address token) external view returns (uint256) {
        return platformPools[token];
    }

    /**
     * @dev Withdraw platform funds (only owner)
     * @param token ERC20 token address (address(0) for native ETH)
     * @param amount Amount to withdraw
     * @param to Recipient address
     */
    function withdrawPlatformFunds(address token, uint256 amount, address to) external onlyOwner {
        require(to != address(0), "Treasury: Invalid recipient");
        require(amount > 0, "Treasury: Invalid amount");
        require(platformPools[token] >= amount, "Treasury: Insufficient platform pool balance");
        
        // Reduce platform pool balance
        platformPools[token] -= amount;
        
        if (token == address(0)) {
            require(address(this).balance >= amount, "Treasury: Insufficient ETH balance");
            (bool success, ) = to.call{value: amount}("");
            require(success, "Treasury: ETH transfer failed");
        } else {
            IERC20 tokenContract = IERC20(token);
            require(tokenContract.balanceOf(address(this)) >= amount, "Treasury: Insufficient token balance");
            tokenContract.safeTransfer(to, amount);
        }
        
        emit PlatformFundsWithdrawn(token, amount, to);
        emit PlatformPoolUpdated(token, platformPools[token], "Platform funds withdrawal");
    }

    /**
     * @dev Emergency withdraw funds (only owner) - for non-platform funds
     */
    function withdraw(address token, uint256 amount, address to) external onlyOwner {
        require(to != address(0), "Treasury: Invalid recipient");
        require(amount > 0, "Treasury: Invalid amount");
        
        if (token == address(0)) {
            require(address(this).balance >= amount, "Treasury: Insufficient ETH balance");
            (bool success, ) = to.call{value: amount}("");
            require(success, "Treasury: ETH transfer failed");
        } else {
            IERC20 tokenContract = IERC20(token);
            require(tokenContract.balanceOf(address(this)) >= amount, "Treasury: Insufficient token balance");
            tokenContract.safeTransfer(to, amount);
        }
        
        emit FundsWithdrawn(token, amount, to);
    }

    /**
     * @dev Place a stake (called by MarketManager)
     */
    function placeStake(uint256 marketId, address user, address token, uint256 amount, uint256 option) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can place stakes");
        require(amount > 0, "Treasury: Invalid stake amount");
        
        // Update user stake
        userStakes[marketId][user][token] += amount;
        userBetOptions[marketId][user][option] += amount;
        
        // Update pools
        marketPools[marketId][token] += amount;
        optionPools[marketId][option][token] += amount;
    }

    /**
     * @dev Support a market (called by MarketManager)
     */
    function supportMarket(uint256 marketId, address user, address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can support markets");
        require(amount > 0, "Treasury: Invalid support amount");
        
        // Update user support
        userSupports[marketId][user][token] += amount;
        
        // Update support pool
        supportPools[marketId][token] += amount;
    }

    /**
     * @dev Withdraw support (called by MarketManager)
     */
    function withdrawSupport(uint256 marketId, address user, address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can withdraw support");
        require(amount > 0, "Treasury: Invalid withdrawal amount");
        require(userSupports[marketId][user][token] >= amount, "Treasury: Insufficient support balance");
        
        // Update user support
        userSupports[marketId][user][token] -= amount;
        
        // Update support pool
        supportPools[marketId][token] -= amount;
        
        // Transfer funds
        _transferPayment(user, amount, token);
    }

    /**
     * @dev Claim winnings (called by MarketManager)
     */
    function claimWinnings(uint256 marketId, address user, address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can claim winnings");
        require(amount > 0, "Treasury: Invalid winnings amount");
        require(!userClaimed[marketId][user], "Treasury: Already claimed");
        
        userClaimed[marketId][user] = true;
        
        // Transfer funds
        _transferPayment(user, amount, token);
    }

    /**
     * @dev Claim refund (called by MarketManager)
     */
    function claimRefund(uint256 marketId, address user, address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can claim refunds");
        require(amount > 0, "Treasury: Invalid refund amount");
        
        // Transfer funds
        _transferPayment(user, amount, token);
    }

    /**
     * @dev Internal function to transfer payment
     */
    function _transferPayment(address to, uint256 amount, address token) internal {
        if (token == address(0)) {
            (bool success, ) = to.call{value: amount}("");
            require(success, "Treasury: ETH transfer failed");
        } else {
            IERC20(token).safeTransfer(to, amount);
        }
    }

    /**
     * @dev Get user bet amount
     */
    function getUserStake(uint256 marketId, address user, address token) external view returns (uint256) {
        return userStakes[marketId][user][token];
    }

    /**
     * @dev Get user support amount
     */
    function getUserSupport(uint256 marketId, address user, address token) external view returns (uint256) {
        return userSupports[marketId][user][token];
    }

    /**
     * @dev Get option pool amount
     */
    function getOptionPool(uint256 marketId, uint256 option, address token) external view returns (uint256) {
        return optionPools[marketId][option][token];
    }

    /**
     * @dev Get support pool amount
     */
    function getSupportPool(uint256 marketId, address token) external view returns (uint256) {
        return supportPools[marketId][token];
    }

    /**
     * @dev Get total market pool (betting pools + support pools)
     */
    function getMarketPool(uint256 marketId, address token) external view returns (uint256) {
        uint256 bettingPool = marketPools[marketId][token];
        uint256 supportPool = supportPools[marketId][token];
        return bettingPool + supportPool;
    }

    /**
     * @dev Check if user has claimed
     */
    function hasUserClaimed(uint256 marketId, address user) external view returns (bool) {
        return userClaimed[marketId][user];
    }

    /**
     * @dev Check if user has claimed support
     */
    function hasUserSupportClaimed(uint256 marketId, address user) external view returns (bool) {
        return userSupportClaimed[marketId][user];
    }

    /**
     * @dev Get all platform pools info (for UI/monitoring)
     */
    function getAllPlatformPools(address[] calldata tokens) external view returns (uint256[] memory balances) {
        balances = new uint256[](tokens.length);
        for (uint256 i = 0; i < tokens.length; i++) {
            balances[i] = platformPools[tokens[i]];
        }
    }

    /**
     * @dev Transfer creator's platform fee directly to creator
     */
    function transferToCreator(address creator, address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can transfer to creator");
        require(amount > 0, "Treasury: Invalid amount");
        require(creator != address(0), "Treasury: Invalid creator address");
        
        if (token == address(0)) {
            // Native ETH
            require(address(this).balance >= amount, "Treasury: Insufficient ETH balance");
            (bool success, ) = creator.call{value: amount}("");
            require(success, "Treasury: ETH transfer to creator failed");
        } else {
            // ERC20 token
            require(IERC20(token).balanceOf(address(this)) >= amount, "Treasury: Insufficient token balance");
            IERC20(token).safeTransfer(creator, amount);
        }
        
        emit FundsWithdrawn(token, amount, creator);
    }

    /**
     * @dev Get contract balance vs platform pool balance (for debugging)
     */
    function getBalanceInfo(address token) external view returns (uint256 contractBalance, uint256 platformPoolBalance) {
        platformPoolBalance = platformPools[token];
        
        if (token == address(0)) {
            contractBalance = address(this).balance;
        } else {
            contractBalance = IERC20(token).balanceOf(address(this));
        }
    }

    /**
     * @dev Receive ETH
     */
    receive() external payable {}
}

