contracts/AdminManger.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

// ========================================
// ADMIN MANAGER CONTRACT
// ========================================
// This contract handles all admin functions:
// - Token whitelist management
// - Wallet blacklist management  
// - Market deletion
// - Settings (durations, restrictions)
// - Links to Analytics & Verification contracts
//
// OWNER CALLS THIS CONTRACT for all admin tasks
// ========================================

import "@openzeppelin/contracts/access/Ownable.sol";

// Interface to interact with EventPool contract
interface IEventPool {
    enum MarketState {
        Active,
        Ended,
        Resolved,
        Cancelled,
        Deleted
    }
    
    struct Market {
        address creator;
        string ipfsHash;
        bool isMultiOption;
        uint256 maxOptions;
        address paymentToken;
        uint256 minStake;
        uint256 creatorDeposit;
        uint256 creatorOutcome;
        uint256 startTime;
        uint256 stakeEndTime;
        uint256 endTime;
        uint256 resolutionEndTime;
        MarketState state;
        uint256 winningOption;
        bool isResolved;
    }
    
    function markets(uint256) external view returns (Market memory);
    function nextMarketId() external view returns (uint256);
    function isActiveMarket(uint256) external view returns (bool);
    function markMarketDeleted(uint256 marketId) external;
    function markMarketPermanentlyRemoved(uint256 marketId) external;
}

contract AdminManager is Ownable {
    
    address public eventPool;
    
    // Supported tokens
    mapping(address => bool) public supportedTokens;
    mapping(address => string) public tokenSymbols;
    address[] public supportedTokenList;
    
    // Blacklisted wallets
    mapping(address => bool) public blacklistedWallets;
    address[] public blacklistedAddresses;
    
    // Deletion tracking
    uint256[] public deletedMarkets;
    mapping(uint256 => uint256) public deletionTimestamp;
    uint256[] public permanentlyRemovedMarkets;
    mapping(uint256 => bool) public isMarketDeleted;
    
    // Settings
    uint256 public minMarketDurationMinutes = 5;
    bool public bettingRestrictionEnabled = false;
    uint256 public bettingRestrictionMinutes = 5;
    
    // Analytics and Verification contracts
    address public analytics;
    address public verification;
    
    // Events
    event EventPoolSet(address indexed eventPool);
    event TokenAdded(address indexed token, string symbol);
    event TokenRemoved(address indexed token);
    event WalletBlacklisted(address indexed wallet, bool blacklisted);
    event MarketDeleted(uint256 indexed marketId, string reason);
    event MarketPermanentlyRemoved(uint256 indexed marketId);
    event AnalyticsSet(address indexed analytics);
    event VerificationSet(address indexed verification);
    event MinMarketDurationSet(uint256 duration);
    event StakingRestrictionSet(bool enabled, uint256 restrictionMinutes);
    
    modifier onlyEventPool() {
        require(msg.sender == eventPool, "AdminManager: Only EventPool");
        _;
    }
    
    constructor(address initialOwner) Ownable(initialOwner) {
        // Set native PEPU token
        tokenSymbols[address(0)] = "PEPU";
        supportedTokens[address(0)] = true;
        supportedTokenList.push(address(0));
    }
    
    /**
     * @dev Set the EventPool contract address (one-time setup)
     */
    function setEventPool(address _eventPool) external onlyOwner {
        require(_eventPool != address(0), "AdminManager: Invalid address");
        require(eventPool == address(0), "AdminManager: EventPool already set");
        eventPool = _eventPool;
        emit EventPoolSet(_eventPool);
    }
    
    // ============ TOKEN MANAGEMENT ============
    
    function addSupportedToken(address token, string memory symbol) external onlyOwner {
        require(token != address(0), "AdminManager: Invalid token address");
        require(bytes(symbol).length > 0, "AdminManager: Invalid token symbol");
        require(!supportedTokens[token], "AdminManager: Token already supported");
        
        supportedTokens[token] = true;
        tokenSymbols[token] = symbol;
        supportedTokenList.push(token);
        
        emit TokenAdded(token, symbol);
    }
    
    function removeSupportedToken(address token) external onlyOwner {
        require(token != address(0), "AdminManager: Cannot remove native token");
        require(supportedTokens[token], "AdminManager: Token not supported");
        
        supportedTokens[token] = false;
        delete tokenSymbols[token];
        
        // Remove from array
        for (uint256 i = 0; i < supportedTokenList.length; i++) {
            if (supportedTokenList[i] == token) {
                supportedTokenList[i] = supportedTokenList[supportedTokenList.length - 1];
                supportedTokenList.pop();
                break;
            }
        }
        
        emit TokenRemoved(token);
    }
    
    function getSupportedTokens() external view returns (address[] memory tokens, string[] memory symbols) {
        uint256 length = supportedTokenList.length;
        tokens = new address[](length);
        symbols = new string[](length);
        
        for (uint256 i = 0; i < length; i++) {
            tokens[i] = supportedTokenList[i];
            symbols[i] = tokenSymbols[supportedTokenList[i]];
        }
    }
    
    function isTokenSupported(address token) external view returns (bool) {
        return supportedTokens[token];
    }
    
    function getTokenSymbol(address token) external view returns (string memory) {
        return tokenSymbols[token];
    }
    
    // ============ BLACKLIST MANAGEMENT ============
    
    function setWalletBlacklist(address wallet, bool blacklisted) external onlyOwner {
        require(wallet != address(0), "AdminManager: Invalid wallet address");
        
        bool wasBlacklisted = blacklistedWallets[wallet];
        blacklistedWallets[wallet] = blacklisted;
        
        if (blacklisted && !wasBlacklisted) {
            blacklistedAddresses.push(wallet);
        } else if (!blacklisted && wasBlacklisted) {
            for (uint256 i = 0; i < blacklistedAddresses.length; i++) {
                if (blacklistedAddresses[i] == wallet) {
                    blacklistedAddresses[i] = blacklistedAddresses[blacklistedAddresses.length - 1];
                    blacklistedAddresses.pop();
                    break;
                }
            }
        }
        
        emit WalletBlacklisted(wallet, blacklisted);
    }
    
    function getBlacklistedAddresses() external view returns (address[] memory) {
        return blacklistedAddresses;
    }
    
    function isWalletBlacklisted(address wallet) external view returns (bool) {
        return blacklistedWallets[wallet];
    }
    
    // ============ MARKET DELETION ============
    
    function deleteMarket(uint256 marketId, string memory reason) external onlyOwner {
        require(eventPool != address(0), "AdminManager: EventPool not set");
        require(!isMarketDeleted[marketId], "AdminManager: Already deleted");
        
        // Mark as deleted
        isMarketDeleted[marketId] = true;
        deletionTimestamp[marketId] = block.timestamp;
        deletedMarkets.push(marketId);
        
        emit MarketDeleted(marketId, reason);
    }
    
    function permanentlyRemoveMarket(uint256 marketId) external onlyOwner {
        require(isMarketDeleted[marketId], "AdminManager: Market not deleted");
        require(block.timestamp >= deletionTimestamp[marketId] + 30 days, "AdminManager: Claim period not ended");
        
        permanentlyRemovedMarkets.push(marketId);
        
        emit MarketPermanentlyRemoved(marketId);
    }
    
    function getDeletedMarkets() external view returns (uint256[] memory) {
        return deletedMarkets;
    }
    
    function getMarketsReadyForRemoval() external view returns (uint256[] memory) {
        uint256[] memory readyMarkets = new uint256[](deletedMarkets.length);
        uint256 count = 0;
        
        for (uint256 i = 0; i < deletedMarkets.length; i++) {
            uint256 marketId = deletedMarkets[i];
            if (isMarketDeleted[marketId] && 
                block.timestamp >= deletionTimestamp[marketId] + 30 days) {
                readyMarkets[count] = marketId;
                count++;
            }
        }
        
        uint256[] memory result = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = readyMarkets[i];
        }
        
        return result;
    }
    
    function getPermanentlyRemovedMarkets() external view returns (uint256[] memory) {
        return permanentlyRemovedMarkets;
    }
    
    // ============ SETTINGS MANAGEMENT ============
    
    function setAnalytics(address _analytics) external onlyOwner {
        analytics = _analytics;
        emit AnalyticsSet(_analytics);
    }
    
    function setVerification(address _verification) external onlyOwner {
        verification = _verification;
        emit VerificationSet(_verification);
    }
    
    function setMinMarketDuration(uint256 _minMarketDurationMinutes) external onlyOwner {
        require(_minMarketDurationMinutes > 0, "AdminManager: Invalid minimum duration");
        minMarketDurationMinutes = _minMarketDurationMinutes;
        emit MinMarketDurationSet(_minMarketDurationMinutes);
    }
    
    function setStakingRestriction(bool _enabled, uint256 _restrictionMinutes) external onlyOwner {
        bettingRestrictionEnabled = _enabled;
        if (_enabled) {
            require(_restrictionMinutes > 0, "AdminManager: Invalid restriction duration");
            bettingRestrictionMinutes = _restrictionMinutes;
        }
        emit StakingRestrictionSet(_enabled, _restrictionMinutes);
    }
}

contracts/P2PAnalytics.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./P2PMarketManager.sol";

contract MetricsHub is Ownable {
    
    // MarketManager reference
    address payable public marketManager;
    
    // User statistics
    struct UserStats {
        uint256 totalStakesPlaced;
        uint256 totalStakesWon;
        uint256 totalStakesLost;
        uint256 totalWinnings;
        uint256 totalLosses;
        uint256 totalSupportDonated;
        uint256 marketsCreated;
        uint256 marketsWon;
        uint256 marketsLost;
        uint256 favoriteOption; // Most staked on option
        uint256 lastActivity;
    }
    
    // Market statistics
    struct MarketStats {
        uint256 totalVolume;
        uint256 totalStakers;
        uint256 totalSupporters;
        uint256 averageStakeSize;
        uint256 largestStake;
        uint256 mostPopularOption;
        uint256 resolutionTime;
        bool wasResolved;
        uint256 creatorWinnings;
    }
    
    // Global statistics
    struct GlobalStats {
        uint256 totalMarkets;
        uint256 totalVolume;
        uint256 totalStakers;
        uint256 totalSupporters;
        uint256 totalWinnings;
        uint256 averageMarketSize;
        uint256 mostActiveToken;
        uint256 averageResolutionTime;
    }
    
    // Mappings
    mapping(address => UserStats) public userStats;
    mapping(uint256 => MarketStats) public marketStats;
    mapping(address => uint256[]) public userMarkets;
    mapping(address => uint256[]) public userStakes;
    mapping(address => uint256[]) public userSupports;
    mapping(address => mapping(uint256 => uint256)) public userOptionStats; // user => option => count
    
    // Events
    event UserStatsUpdated(address indexed user, uint256 totalStakes, uint256 winnings);
    event MarketStatsUpdated(uint256 indexed marketId, uint256 volume, uint256 stakers);
    event GlobalStatsUpdated(uint256 totalMarkets, uint256 totalVolume);
    
    constructor(address initialOwner, address _marketManager) Ownable(initialOwner) {
        marketManager = payable(_marketManager);
    }
    
    /**
     * @dev Update market manager address
     */
    function setMarketManager(address _marketManager) external onlyOwner {
        marketManager = payable(_marketManager);
    }
    
    /**
     * @dev Track stake placement
     */
    function trackStake(uint256 marketId, address user, uint256 option, uint256 amount) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        UserStats storage stats = userStats[user];
        stats.totalStakesPlaced++;
        stats.totalLosses += amount; // Will be updated when resolved
        stats.lastActivity = block.timestamp;
        
        // Track option preference
        userOptionStats[user][option]++;
        if (userOptionStats[user][option] > userOptionStats[user][stats.favoriteOption]) {
            stats.favoriteOption = option;
        }
        
        // Update market stats
        MarketStats storage marketStat = marketStats[marketId];
        marketStat.totalVolume += amount;
        marketStat.totalStakers = EventPool(marketManager).getStakerCount(marketId);
        marketStat.averageStakeSize = marketStat.totalStakers > 0 ? marketStat.totalVolume / marketStat.totalStakers : 0;
        
        if (amount > marketStat.largestStake) {
            marketStat.largestStake = amount;
        }
        
        // Track user's markets
        if (userMarkets[user].length == 0 || userMarkets[user][userMarkets[user].length - 1] != marketId) {
            userMarkets[user].push(marketId);
        }
        userStakes[user].push(marketId);
        
        emit UserStatsUpdated(user, stats.totalStakesPlaced, stats.totalWinnings);
        emit MarketStatsUpdated(marketId, marketStat.totalVolume, marketStat.totalStakers);
    }
    
    /**
     * @dev Track support donation
     */
    function trackSupport(uint256 marketId, address user, uint256 amount) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        UserStats storage stats = userStats[user];
        stats.totalSupportDonated += amount;
        stats.lastActivity = block.timestamp;
        
        // Update market stats
        MarketStats storage marketStat = marketStats[marketId];
        marketStat.totalSupporters = EventPool(marketManager).getSupporterCount(marketId);
        
        userSupports[user].push(marketId);
        
        emit UserStatsUpdated(user, stats.totalStakesPlaced, stats.totalWinnings);
    }
    
    /**
     * @dev Track market creation
     */
    function trackMarketCreation(uint256 marketId, address creator) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        UserStats storage stats = userStats[creator];
        stats.marketsCreated++;
        stats.lastActivity = block.timestamp;
        
        userMarkets[creator].push(marketId);
        
        emit UserStatsUpdated(creator, stats.totalStakesPlaced, stats.totalWinnings);
    }
    
    /**
     * @dev Track market resolution
     */
    function trackMarketResolution(uint256 marketId, uint256 winningOption) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        MarketStats storage marketStat = marketStats[marketId];
        marketStat.wasResolved = true;
        marketStat.resolutionTime = block.timestamp;
        marketStat.mostPopularOption = winningOption;
        
        // Update global stats (note: this is expensive, consider caching)
        // GlobalStats storage global = getGlobalStats();
        // global.totalMarkets++;
        // global.totalVolume += marketStat.totalVolume;
        
        emit GlobalStatsUpdated(0, 0); // Placeholder - global stats calculation is expensive
    }
    
    /**
     * @dev Track user winnings
     */
    function trackWinnings(uint256 marketId, address user, uint256 winnings) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        UserStats storage stats = userStats[user];
        stats.totalStakesWon++;
        stats.totalWinnings += winnings;
        stats.lastActivity = block.timestamp;
        
        // Check if user won the market they created
        EventPool.Market memory market = EventPool(marketManager).getMarket(marketId);
        if (market.creator == user) {
            stats.marketsWon++;
            marketStats[marketId].creatorWinnings = winnings;
        }
        
        emit UserStatsUpdated(user, stats.totalStakesPlaced, stats.totalWinnings);
    }
    
    /**
     * @dev Track user loss
     */
    function trackLoss(uint256 marketId, address user, uint256 loss) external {
        require(msg.sender == marketManager, "Analytics: Only MarketManager can track");
        
        UserStats storage stats = userStats[user];
        stats.totalStakesLost++;
        stats.lastActivity = block.timestamp;
        
        // Check if user lost the market they created
        EventPool.Market memory market = EventPool(marketManager).getMarket(marketId);
        if (market.creator == user) {
            stats.marketsLost++;
        }
        
        emit UserStatsUpdated(user, stats.totalStakesPlaced, stats.totalWinnings);
    }
    
    /**
     * @dev Get user statistics
     */
    function getUserStats(address user) external view returns (UserStats memory) {
        return userStats[user];
    }
    
    /**
     * @dev Get market statistics
     */
    function getMarketStats(uint256 marketId) external view returns (MarketStats memory) {
        return marketStats[marketId];
    }
    
    /**
     * @dev Get global statistics
     */
    function getGlobalStats() public view returns (GlobalStats memory) {
        uint256 totalMarkets = EventPool(marketManager).getNextMarketId() - 1;
        uint256 totalVolume = 0;
        uint256 totalStakers = 0;
        uint256 totalSupporters = 0;
        
        // Calculate totals (this is expensive, consider caching)
        for (uint256 i = 1; i <= totalMarkets; i++) {
            MarketStats memory market = marketStats[i];
            totalVolume += market.totalVolume;
            totalStakers += market.totalStakers;
            totalSupporters += market.totalSupporters;
        }
        
        return GlobalStats({
            totalMarkets: totalMarkets,
            totalVolume: totalVolume,
            totalStakers: totalStakers,
            totalSupporters: totalSupporters,
            totalWinnings: 0, // Would need to track this
            averageMarketSize: totalMarkets > 0 ? totalVolume / totalMarkets : 0,
            mostActiveToken: 0, // Would need to track this
            averageResolutionTime: 0 // Would need to track this
        });
    }
    
    /**
     * @dev Get user's market history
     */
    function getUserMarkets(address user) external view returns (uint256[] memory) {
        return userMarkets[user];
    }
    
    /**
     * @dev Get user's stake history
     */
    function getUserStakes(address user) external view returns (uint256[] memory) {
        return userStakes[user];
    }
    
    /**
     * @dev Get user's support history
     */
    function getUserSupports(address user) external view returns (uint256[] memory) {
        return userSupports[user];
    }
    
    /**
     * @dev Get user's win rate
     */
    function getUserWinRate(address user) external view returns (uint256) {
        UserStats memory stats = userStats[user];
        if (stats.totalStakesPlaced == 0) return 0;
        return (stats.totalStakesWon * 100) / stats.totalStakesPlaced;
    }
    
    /**
     * @dev Get user's favorite option
     */
    function getUserFavoriteOption(address user) external view returns (uint256) {
        return userStats[user].favoriteOption;
    }
    
    /**
     * @dev Get markets filtered by creator
     */
    function getMarketsByCreator(address creator) external view returns (uint256[] memory) {
        return userMarkets[creator];
    }
    
    /**
     * @dev Get markets filtered by token
     */
    function getMarketsByToken(address token) external view returns (uint256[] memory) {
        uint256[] memory filteredMarkets = new uint256[](0);
        uint256 nextMarketId = EventPool(marketManager).getNextMarketId();
        
        for (uint256 i = 1; i < nextMarketId; i++) {
            EventPool.Market memory market = EventPool(marketManager).getMarket(i);
            if (market.paymentToken == token) {
                // Add to filtered array (this is expensive, consider events)
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = i;
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get active markets filtered by minimum volume
     */
    function getActiveMarketsByMinVolume(uint256 minVolume) external view returns (uint256[] memory) {
        uint256[] memory filteredMarkets = new uint256[](0);
        uint256 nextMarketId = EventPool(marketManager).getNextMarketId();
        
        for (uint256 i = 1; i < nextMarketId; i++) {
            EventPool.Market memory market = EventPool(marketManager).getMarket(i);
            if (market.state == EventPool.MarketState.Active && marketStats[i].totalVolume >= minVolume) {
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = i;
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get markets filtered by state
     */
    function getMarketsByState(EventPool.MarketState state) external view returns (uint256[] memory) {
        uint256[] memory filteredMarkets = new uint256[](0);
        uint256 nextMarketId = EventPool(marketManager).getNextMarketId();
        
        for (uint256 i = 1; i < nextMarketId; i++) {
            EventPool.Market memory market = EventPool(marketManager).getMarket(i);
            if (market.state == state) {
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = i;
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get markets filtered by date range
     */
    function getMarketsByDateRange(uint256 startTime, uint256 endTime) external view returns (uint256[] memory) {
        uint256[] memory filteredMarkets = new uint256[](0);
        uint256 nextMarketId = EventPool(marketManager).getNextMarketId();
        
        for (uint256 i = 1; i < nextMarketId; i++) {
            EventPool.Market memory market = EventPool(marketManager).getMarket(i);
            if (market.startTime >= startTime && market.startTime <= endTime) {
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = i;
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get markets filtered by multiple criteria
     */
    function getMarketsFiltered(
        address creator,
        address token,
        uint256 minVolume,
        uint256 maxVolume,
        EventPool.MarketState state,
        uint256 startTime,
        uint256 endTime
    ) external view returns (uint256[] memory) {
        uint256[] memory filteredMarkets = new uint256[](0);
        uint256 nextMarketId = EventPool(marketManager).getNextMarketId();
        
        for (uint256 i = 1; i < nextMarketId; i++) {
            EventPool.Market memory market = EventPool(marketManager).getMarket(i);
            MarketStats memory stats = marketStats[i];
            
            bool matches = true;
            
            // Filter by creator
            if (creator != address(0) && market.creator != creator) {
                matches = false;
            }
            
            // Filter by token
            if (token != address(0) && market.paymentToken != token) {
                matches = false;
            }
            
            // Filter by volume range
            if (minVolume > 0 && stats.totalVolume < minVolume) {
                matches = false;
            }
            if (maxVolume > 0 && stats.totalVolume > maxVolume) {
                matches = false;
            }
            
            // Filter by state
            if (state != EventPool.MarketState.Active && market.state != state) {
                matches = false;
            }
            
            // Filter by date range
            if (startTime > 0 && market.startTime < startTime) {
                matches = false;
            }
            if (endTime > 0 && market.startTime > endTime) {
                matches = false;
            }
            
            if (matches) {
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = i;
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get user's markets filtered by state
     */
    function getUserMarketsByState(address user, EventPool.MarketState state) external view returns (uint256[] memory) {
        uint256[] memory userMarketsList = userMarkets[user];
        uint256[] memory filteredMarkets = new uint256[](0);
        
        for (uint256 i = 0; i < userMarketsList.length; i++) {
            EventPool.Market memory market = EventPool(marketManager).getMarket(userMarketsList[i]);
            if (market.state == state) {
                uint256[] memory temp = new uint256[](filteredMarkets.length + 1);
                for (uint256 j = 0; j < filteredMarkets.length; j++) {
                    temp[j] = filteredMarkets[j];
                }
                temp[filteredMarkets.length] = userMarketsList[i];
                filteredMarkets = temp;
            }
        }
        return filteredMarkets;
    }
    
    /**
     * @dev Get active markets sorted by volume (descending)
     */
    function getActiveMarketsByVolume(uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        uint256 nextMarketId = EventPool(marketManager).getNextMarketId();
        uint256[] memory marketIds = new uint256[](0);
        uint256[] memory volumes = new uint256[](0);
        
        // Collect only ACTIVE markets
        for (uint256 i = 1; i < nextMarketId; i++) {
            EventPool.Market memory market = EventPool(marketManager).getMarket(i);
            if (market.state == EventPool.MarketState.Active) {
                // Add to arrays
                uint256[] memory tempIds = new uint256[](marketIds.length + 1);
                uint256[] memory tempVolumes = new uint256[](volumes.length + 1);
                
                for (uint256 j = 0; j < marketIds.length; j++) {
                    tempIds[j] = marketIds[j];
                    tempVolumes[j] = volumes[j];
                }
                tempIds[marketIds.length] = i;
                tempVolumes[volumes.length] = marketStats[i].totalVolume;
                
                marketIds = tempIds;
                volumes = tempVolumes;
            }
        }
        
        // Simple bubble sort (expensive on-chain, consider off-chain)
        for (uint256 i = 0; i < marketIds.length - 1; i++) {
            for (uint256 j = 0; j < marketIds.length - i - 1; j++) {
                if (volumes[j] < volumes[j + 1]) {
                    // Swap volumes
                    uint256 tempVolume = volumes[j];
                    volumes[j] = volumes[j + 1];
                    volumes[j + 1] = tempVolume;
                    
                    // Swap market IDs
                    uint256 tempId = marketIds[j];
                    marketIds[j] = marketIds[j + 1];
                    marketIds[j + 1] = tempId;
                }
            }
        }
        
        // Return limited results
        uint256 resultLength = limit > 0 && limit < marketIds.length ? limit : marketIds.length;
        uint256[] memory resultIds = new uint256[](resultLength);
        uint256[] memory resultVolumes = new uint256[](resultLength);
        
        for (uint256 i = 0; i < resultLength; i++) {
            resultIds[i] = marketIds[i];
            resultVolumes[i] = volumes[i];
        }
        
        return (resultIds, resultVolumes);
    }
    
    /**
     * @dev Get active markets by staker count (most stakers)
     */
    function getActiveMarketsByStakerCount(uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        uint256 nextMarketId = EventPool(marketManager).getNextMarketId();
        uint256[] memory marketIds = new uint256[](0);
        uint256[] memory stakerCounts = new uint256[](0);
        
        // Collect only ACTIVE markets
        for (uint256 i = 1; i < nextMarketId; i++) {
            EventPool.Market memory market = EventPool(marketManager).getMarket(i);
            if (market.state == EventPool.MarketState.Active) {
                uint256 stakerCount = EventPool(marketManager).getStakerCount(i);
                
                // Add to arrays
                uint256[] memory tempIds = new uint256[](marketIds.length + 1);
                uint256[] memory tempCounts = new uint256[](stakerCounts.length + 1);
                
                for (uint256 j = 0; j < marketIds.length; j++) {
                    tempIds[j] = marketIds[j];
                    tempCounts[j] = stakerCounts[j];
                }
                tempIds[marketIds.length] = i;
                tempCounts[stakerCounts.length] = stakerCount;
                
                marketIds = tempIds;
                stakerCounts = tempCounts;
            }
        }
        
        // Sort by staker count (bubble sort)
        for (uint256 i = 0; i < marketIds.length - 1; i++) {
            for (uint256 j = 0; j < marketIds.length - i - 1; j++) {
                if (stakerCounts[j] < stakerCounts[j + 1]) {
                    // Swap counts
                    uint256 tempCount = stakerCounts[j];
                    stakerCounts[j] = stakerCounts[j + 1];
                    stakerCounts[j + 1] = tempCount;
                    
                    // Swap market IDs
                    uint256 tempId = marketIds[j];
                    marketIds[j] = marketIds[j + 1];
                    marketIds[j + 1] = tempId;
                }
            }
        }
        
        // Return limited results
        uint256 resultLength = limit > 0 && limit < marketIds.length ? limit : marketIds.length;
        uint256[] memory resultIds = new uint256[](resultLength);
        uint256[] memory resultCounts = new uint256[](resultLength);
        
        for (uint256 i = 0; i < resultLength; i++) {
            resultIds[i] = marketIds[i];
            resultCounts[i] = stakerCounts[i];
        }
        
        return (resultIds, resultCounts);
    }
    
    /**
     * @dev Get active markets by total participants (stakers + supporters)
     */
    function getActiveMarketsByParticipants(uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        uint256 nextMarketId = EventPool(marketManager).getNextMarketId();
        uint256[] memory marketIds = new uint256[](0);
        uint256[] memory participantCounts = new uint256[](0);
        
        // Collect only ACTIVE markets
        for (uint256 i = 1; i < nextMarketId; i++) {
            EventPool.Market memory market = EventPool(marketManager).getMarket(i);
            if (market.state == EventPool.MarketState.Active) {
            uint256 stakerCount = EventPool(marketManager).getStakerCount(i);
            uint256 supporterCount = EventPool(marketManager).getSupporterCount(i);
            uint256 totalParticipants = stakerCount + supporterCount;
                
                // Add to arrays
                uint256[] memory tempIds = new uint256[](marketIds.length + 1);
                uint256[] memory tempCounts = new uint256[](participantCounts.length + 1);
                
                for (uint256 j = 0; j < marketIds.length; j++) {
                    tempIds[j] = marketIds[j];
                    tempCounts[j] = participantCounts[j];
                }
                tempIds[marketIds.length] = i;
                tempCounts[participantCounts.length] = totalParticipants;
                
                marketIds = tempIds;
                participantCounts = tempCounts;
            }
        }
        
        // Sort by total participants (bubble sort)
        for (uint256 i = 0; i < marketIds.length - 1; i++) {
            for (uint256 j = 0; j < marketIds.length - i - 1; j++) {
                if (participantCounts[j] < participantCounts[j + 1]) {
                    // Swap counts
                    uint256 tempCount = participantCounts[j];
                    participantCounts[j] = participantCounts[j + 1];
                    participantCounts[j + 1] = tempCount;
                    
                    // Swap market IDs
                    uint256 tempId = marketIds[j];
                    marketIds[j] = marketIds[j + 1];
                    marketIds[j + 1] = tempId;
                }
            }
        }
        
        // Return limited results
        uint256 resultLength = limit > 0 && limit < marketIds.length ? limit : marketIds.length;
        uint256[] memory resultIds = new uint256[](resultLength);
        uint256[] memory resultCounts = new uint256[](resultLength);
        
        for (uint256 i = 0; i < resultLength; i++) {
            resultIds[i] = marketIds[i];
            resultCounts[i] = participantCounts[i];
        }
        
        return (resultIds, resultCounts);
    }
    
    /**
     * @dev Get recent active markets (created within last N days)
     */
    function getRecentActiveMarkets(uint256 daysBack, uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        uint256 nextMarketId = EventPool(marketManager).getNextMarketId();
        uint256[] memory marketIds = new uint256[](0);
        uint256[] memory timestamps = new uint256[](0);
        
        uint256 cutoffTime = block.timestamp - (daysBack * 1 days);
        
        // Collect only ACTIVE markets created within the time period
        for (uint256 i = 1; i < nextMarketId; i++) {
            EventPool.Market memory market = EventPool(marketManager).getMarket(i);
            if (market.state == EventPool.MarketState.Active && market.startTime >= cutoffTime) {
                // Add to arrays
                uint256[] memory tempIds = new uint256[](marketIds.length + 1);
                uint256[] memory tempTimestamps = new uint256[](timestamps.length + 1);
                
                for (uint256 j = 0; j < marketIds.length; j++) {
                    tempIds[j] = marketIds[j];
                    tempTimestamps[j] = timestamps[j];
                }
                tempIds[marketIds.length] = i;
                tempTimestamps[timestamps.length] = market.startTime;
                
                marketIds = tempIds;
                timestamps = tempTimestamps;
            }
        }
        
        // Sort by creation time (newest first)
        for (uint256 i = 0; i < marketIds.length - 1; i++) {
            for (uint256 j = 0; j < marketIds.length - i - 1; j++) {
                if (timestamps[j] < timestamps[j + 1]) {
                    // Swap timestamps
                    uint256 tempTimestamp = timestamps[j];
                    timestamps[j] = timestamps[j + 1];
                    timestamps[j + 1] = tempTimestamp;
                    
                    // Swap market IDs
                    uint256 tempId = marketIds[j];
                    marketIds[j] = marketIds[j + 1];
                    marketIds[j + 1] = tempId;
                }
            }
        }
        
        // Return limited results
        uint256 resultLength = limit > 0 && limit < marketIds.length ? limit : marketIds.length;
        uint256[] memory resultIds = new uint256[](resultLength);
        uint256[] memory resultTimestamps = new uint256[](resultLength);
        
        for (uint256 i = 0; i < resultLength; i++) {
            resultIds[i] = marketIds[i];
            resultTimestamps[i] = timestamps[i];
        }
        
        return (resultIds, resultTimestamps);
    }
    
    /**
     * @dev Get active markets created today
     */
    function getTodayActiveMarkets(uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        return this.getRecentActiveMarkets(1, limit);
    }
    
    /**
     * @dev Get active markets created this week
     */
    function getThisWeekActiveMarkets(uint256 limit) external view returns (uint256[] memory, uint256[] memory) {
        return this.getRecentActiveMarkets(7, limit);
    }
    
    /**
     * @dev Get top stakers by volume
     */
    function getTopStakers(uint256 limit) external view returns (address[] memory, uint256[] memory) {
        // This would require sorting, which is expensive on-chain
        // Consider implementing off-chain or with events
        address[] memory empty;
        uint256[] memory empty2;
        return (empty, empty2);
    }
}

contracts/P2PMarketManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

// ========================================
// EVENT POOL CONTRACT (MAIN BETTING CONTRACT)
// ========================================
// This contract handles all betting/market functions:
// - Create markets
// - Place stakes/bets
// - Support markets
// - Resolve markets
// - Claim winnings/refunds
//
// USERS CALL THIS CONTRACT for all betting actions
// READS FROM AdminManager for permissions/settings
// ========================================

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./P2PTreasury.sol";
import "./P2PVerification.sol";
import "./P2PAnalytics.sol";

pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./P2PTreasury.sol";
import "./P2PVerification.sol";
import "./P2PAnalytics.sol";

interface IAdminManager {
    function supportedTokens(address) external view returns (bool);
    function tokenSymbols(address) external view returns (string memory);
    function blacklistedWallets(address) external view returns (bool);
    function isMarketDeleted(uint256) external view returns (bool);
    function minMarketDurationMinutes() external view returns (uint256);
    function bettingRestrictionEnabled() external view returns (bool);
    function bettingRestrictionMinutes() external view returns (uint256);
    function analytics() external view returns (address);
    function verification() external view returns (address);
}

contract EventPool is Ownable {
    using SafeERC20 for IERC20;

    // Market states
    enum MarketState {
        Active,
        Ended,
        Resolved,
        Cancelled,
        Deleted
    }

    // Market structure
    struct Market {
        address creator;
        string ipfsHash;
        bool isMultiOption;
        uint256 maxOptions;
        address paymentToken;
        uint256 minStake;
        uint256 creatorDeposit;
        uint256 creatorOutcome;
        uint256 startTime;
        uint256 stakeEndTime;
        uint256 endTime;
        uint256 resolutionEndTime;
        MarketState state;
        uint256 winningOption;
        bool isResolved;
    }

    // Market data
    mapping(uint256 => Market) public markets;
    mapping(uint256 => mapping(address => bool)) public userHasStaked;
    mapping(uint256 => mapping(address => bool)) public userHasSupported;
    mapping(uint256 => mapping(address => uint256)) public userStakeOptions;
    mapping(uint256 => address[]) public marketStakers;
    mapping(uint256 => address[]) public marketSupporters;
    
    // Contracts
    address payable public treasury;
    address public adminManager; // NEW: AdminManager contract
    
    // Market management
    uint256 public nextMarketId = 1;
    uint256 public constant marketCreationFee = 1000000000000000000;
    
    // Fee structure (in basis points)
    uint256 public constant CREATOR_FEE_BPS = 350;
    uint256 public constant VERIFIER_FEE_BPS = 150;
    uint256 public constant PLATFORM_FEE_BPS = 500;
    
    // Active market tracking
    uint256[] public activeMarkets;
    mapping(uint256 => bool) public isActiveMarket;
    
    // Stake history tracking
    mapping(address => uint256[]) public userMarketHistory;
    
    // Verifier resolution tracking
    mapping(uint256 => address[]) public marketResolvingVerifiers;
    
    // Events
    event MarketCreated(
        uint256 indexed marketId,
        address indexed creator,
        string ipfsHash,
        bool isMultiOption,
        uint256 maxOptions,
        address paymentToken,
        uint256 minStake,
        uint256 startTime,
        uint256 stakeEndTime,
        uint256 endTime,
        uint256 resolutionEndTime
    );
    
    event StakePlaced(
        uint256 indexed marketId,
        address indexed user,
        uint256 option,
        uint256 amount
    );
    
    event MarketResolved(uint256 indexed marketId, uint256 winningOption);
    event MarketCancelled(uint256 indexed marketId, string reason);
    event RefundClaimed(uint256 indexed marketId, address indexed user, uint256 amount);
    event WinningsClaimed(uint256 indexed marketId, address indexed user, uint256 amount);

    constructor(
        address initialOwner,
        address payable _treasury,
        address _adminManager
    ) Ownable(initialOwner) {
        treasury = _treasury;
        adminManager = _adminManager;
    }

    // ============ MODIFIERS ============
    
    modifier notBlacklisted() {
        require(!IAdminManager(adminManager).blacklistedWallets(msg.sender), "EventPool: Wallet blacklisted");
        _;
    }
    
    modifier notDeleted(uint256 marketId) {
        require(!IAdminManager(adminManager).isMarketDeleted(marketId), "EventPool: Market deleted");
        _;
    }

    /**
     * @dev Create a new market
     */
    function createMarket(
        string memory ipfsHash,
        bool isMultiOption,
        uint256 maxOptions,
        address paymentToken,
        uint256 minStake,
        uint256 creatorDeposit,
        uint256 creatorOutcome,
        uint256 stakeDurationMinutes,
        uint256 resolutionDurationMinutes
    ) external payable notBlacklisted returns (uint256) {
        require(bytes(ipfsHash).length > 0, "EventPool: Invalid IPFS hash");
        require(maxOptions >= 2 && maxOptions <= 10, "EventPool: Invalid max options");
        require(minStake > 0, "EventPool: Invalid min stake");
        require(creatorDeposit > 0, "EventPool: Invalid creator deposit");
        require(creatorOutcome > 0 && creatorOutcome <= maxOptions, "EventPool: Invalid creator outcome");
        
        // Get settings from AdminManager
        uint256 minDuration = IAdminManager(adminManager).minMarketDurationMinutes();
        require(stakeDurationMinutes >= minDuration, "EventPool: Below minimum stake duration");
        require(resolutionDurationMinutes >= stakeDurationMinutes, "EventPool: Resolution must be after stake period");
        
        // Validate payment token via AdminManager
        if (isMultiOption) {
            require(paymentToken != address(0), "EventPool: Multi-option markets require P2P token");
            require(IAdminManager(adminManager).supportedTokens(paymentToken), "EventPool: Token not supported for multi-option");
        } else {
            if (paymentToken != address(0)) {
                require(IAdminManager(adminManager).supportedTokens(paymentToken), "EventPool: Token not supported");
            }
        }
        
        // Pay market creation fee
        require(msg.value >= marketCreationFee, "EventPool: Insufficient creation fee");
        
        // Calculate timestamps
        uint256 startTime = block.timestamp;
        uint256 stakeEndTime = startTime + (stakeDurationMinutes * 1 minutes);
        uint256 endTime = startTime + (resolutionDurationMinutes * 1 minutes);
        uint256 resolutionEndTime = endTime + 48 hours;
        
        // Create market
        uint256 marketId = nextMarketId++;
        markets[marketId] = Market({
            creator: msg.sender,
            ipfsHash: ipfsHash,
            isMultiOption: isMultiOption,
            maxOptions: maxOptions,
            paymentToken: paymentToken,
            minStake: minStake,
            creatorDeposit: creatorDeposit,
            creatorOutcome: creatorOutcome,
            startTime: startTime,
            stakeEndTime: stakeEndTime,
            endTime: endTime,
            resolutionEndTime: resolutionEndTime,
            state: MarketState.Active,
            winningOption: 0,
            isResolved: false
        });
        
        // Add to active markets
        activeMarkets.push(marketId);
        isActiveMarket[marketId] = true;
        
        // Transfer creator deposit and send fee to owner
        if (paymentToken == address(0)) {
            require(msg.value >= marketCreationFee + creatorDeposit, "EventPool: Insufficient PEPU");
            (bool success, ) = owner().call{value: marketCreationFee}("");
            require(success, "EventPool: Fee transfer to owner failed");
            (bool treasurySuccess, ) = treasury.call{value: creatorDeposit}("");
            require(treasurySuccess, "EventPool: Creator deposit transfer to Treasury failed");
        } else {
            require(msg.value >= marketCreationFee, "EventPool: Insufficient PEPU for creation fee");
            (bool success, ) = owner().call{value: marketCreationFee}("");
            require(success, "EventPool: Fee transfer to owner failed");
            IERC20(paymentToken).safeTransferFrom(msg.sender, treasury, creatorDeposit);
        }

        // Creator's deposit becomes their stake
        userHasStaked[marketId][msg.sender] = true;
        userStakeOptions[marketId][msg.sender] = creatorOutcome;
        userMarketHistory[msg.sender].push(marketId);
        
        // Call Treasury to handle creator's stake
        PoolVault(treasury).placeStake(marketId, msg.sender, paymentToken, creatorDeposit, creatorOutcome);
        
        // Track analytics (get address from AdminManager)
        address analytics = IAdminManager(adminManager).analytics();
        if (analytics != address(0)) {
            MetricsHub(analytics).trackMarketCreation(marketId, msg.sender);
            MetricsHub(analytics).trackStake(marketId, msg.sender, creatorOutcome, creatorDeposit);
        }
        
        emit MarketCreated(
            marketId,
            msg.sender,
            ipfsHash,
            isMultiOption,
            maxOptions,
            paymentToken,
            minStake,
            startTime,
            stakeEndTime,
            endTime,
            resolutionEndTime
        );
        
        return marketId;
    }

    /**
     * @dev Place a bet on a specific option (for PEPU markets)
     */
    function placeStake(uint256 marketId, uint256 option) external payable notDeleted(marketId) {
        Market storage market = markets[marketId];
        require(market.state == MarketState.Active, "EventPool: Market not active");
        
        // Check betting restriction from AdminManager
        if (IAdminManager(adminManager).bettingRestrictionEnabled()) {
            uint256 restrictionMinutes = IAdminManager(adminManager).bettingRestrictionMinutes();
            require(block.timestamp < market.stakeEndTime - (restrictionMinutes * 1 minutes), "EventPool: Staking period ended");
        }
        
        require(option > 0 && option <= market.maxOptions, "EventPool: Invalid option");
        require(market.paymentToken == address(0), "EventPool: This market uses tokens, use placeBetWithToken");
        
        uint256 amount = msg.value;
        require(amount >= market.minStake, "EventPool: Below minimum stake");
        
        // Transfer ETH to Treasury first
        (bool success, ) = treasury.call{value: amount}("");
        require(success, "EventPool: ETH transfer to Treasury failed");
        
        // Update user stake tracking
        if (!userHasStaked[marketId][msg.sender]) {
            marketStakers[marketId].push(msg.sender);
            userMarketHistory[msg.sender].push(marketId);
        }
        userHasStaked[marketId][msg.sender] = true;
        userStakeOptions[marketId][msg.sender] = option;
        
        // Call Treasury to handle funds accounting
        PoolVault(treasury).placeStake(marketId, msg.sender, market.paymentToken, amount, option);
        
        // Track analytics
        address analytics = IAdminManager(adminManager).analytics();
        if (analytics != address(0)) {
            MetricsHub(analytics).trackStake(marketId, msg.sender, option, amount);
        }
        
        emit StakePlaced(marketId, msg.sender, option, amount);
    }

    /**
     * @dev Place a bet on a specific option (for token markets)
     */
    function placeStakeWithToken(uint256 marketId, uint256 option, uint256 amount) external notDeleted(marketId) {
        Market storage market = markets[marketId];
        require(market.state == MarketState.Active, "EventPool: Market not active");
        
        if (IAdminManager(adminManager).bettingRestrictionEnabled()) {
            uint256 restrictionMinutes = IAdminManager(adminManager).bettingRestrictionMinutes();
            require(block.timestamp < market.stakeEndTime - (restrictionMinutes * 1 minutes), "EventPool: Staking period ended");
        }
        
        require(option > 0 && option <= market.maxOptions, "EventPool: Invalid option");
        require(market.paymentToken != address(0), "EventPool: This market uses PEPU, use placeBet");
        require(amount >= market.minStake, "EventPool: Below minimum stake");
        
        // Transfer tokens to Treasury
        IERC20(market.paymentToken).safeTransferFrom(msg.sender, treasury, amount);
        
        // Update user stake tracking
        if (!userHasStaked[marketId][msg.sender]) {
            marketStakers[marketId].push(msg.sender);
            userMarketHistory[msg.sender].push(marketId);
        }
        userHasStaked[marketId][msg.sender] = true;
        userStakeOptions[marketId][msg.sender] = option;
        
        // Call Treasury to handle funds
        PoolVault(treasury).placeStake(marketId, msg.sender, market.paymentToken, amount, option);
        
        // Track analytics
        address analytics = IAdminManager(adminManager).analytics();
        if (analytics != address(0)) {
            MetricsHub(analytics).trackStake(marketId, msg.sender, option, amount);
        }
        
        emit StakePlaced(marketId, msg.sender, option, amount);
    }

    /**
     * @dev Support a market without betting on any option
     */
    function supportMarket(uint256 marketId, uint256 amount) external payable notBlacklisted notDeleted(marketId) {
        Market storage market = markets[marketId];
        require(market.state == MarketState.Active, "EventPool: Market not active");
        require(block.timestamp < market.stakeEndTime, "EventPool: Support period ended");
        require(amount > 0, "EventPool: Support amount must be greater than 0");
        
        if (market.paymentToken == address(0)) {
            require(msg.value == amount, "EventPool: Incorrect ETH amount");
            require(msg.value > 0, "EventPool: Support amount must be greater than 0");
            
            (bool success, ) = treasury.call{value: msg.value}("");
            require(success, "EventPool: ETH transfer to treasury failed");
        } else {
            require(msg.value == 0, "EventPool: No ETH should be sent for token support");
            IERC20(market.paymentToken).safeTransferFrom(msg.sender, treasury, amount);
        }
        
        if (!userHasSupported[marketId][msg.sender]) {
            marketSupporters[marketId].push(msg.sender);
        }
        userHasSupported[marketId][msg.sender] = true;
        
        PoolVault(treasury).supportMarket(marketId, msg.sender, market.paymentToken, amount);
        
        address analytics = IAdminManager(adminManager).analytics();
        if (analytics != address(0)) {
            MetricsHub(analytics).trackSupport(marketId, msg.sender, amount);
        }
    }

    /**
     * @dev Withdraw support from a market
     */
    function withdrawSupport(uint256 marketId) external {
        Market storage market = markets[marketId];
        require(userHasSupported[marketId][msg.sender], "EventPool: No support to withdraw");
        
        if (market.state == MarketState.Active) {
            require(block.timestamp < market.stakeEndTime - 12 hours, "EventPool: Cannot withdraw within 12 hours of staking end");
        } else if (market.state == MarketState.Deleted || market.state == MarketState.Cancelled) {
            // Can withdraw anytime
        } else {
            revert("EventPool: Cannot withdraw in current market state");
        }
        
        uint256 supportAmount = PoolVault(treasury).getUserSupport(marketId, msg.sender, market.paymentToken);
        require(supportAmount > 0, "EventPool: No support to withdraw");
        
        PoolVault(treasury).withdrawSupport(marketId, msg.sender, market.paymentToken, supportAmount);
        userHasSupported[marketId][msg.sender] = false;
    }

    /**
     * @dev End the betting period
     */
    function endMarket(uint256 marketId) external {
        Market storage market = markets[marketId];
        require(market.state == MarketState.Active, "EventPool: Market not active");
        require(block.timestamp >= market.endTime, "EventPool: Resolution time not reached");
        
        market.state = MarketState.Ended;
        
        if (isActiveMarket[marketId]) {
            _removeFromActiveMarkets(marketId);
        }
    }

    /**
     * @dev Resolve market with winning option
     */
    function resolveMarket(uint256 marketId, uint256 _winningOption) external {
        Market storage market = markets[marketId];
        require(market.state == MarketState.Ended, "EventPool: Market not in ended state");
        require(_winningOption > 0 && _winningOption <= market.maxOptions, "EventPool: Invalid winning option");
        require(!market.isResolved, "EventPool: Already resolved");
        
        if (block.timestamp >= market.resolutionEndTime) {
            _cancelMarket(marketId, "Resolution period expired");
            return;
        }
        
        address verification = IAdminManager(adminManager).verification();
        (bool resolved, uint256 votedOption) = ValidationCore(verification).isResolved(marketId);
        if (resolved && votedOption == _winningOption) {
            market.winningOption = _winningOption;
            market.isResolved = true;
            market.state = MarketState.Resolved;
            
            _trackResolvingVerifiers(marketId, _winningOption);
            _distributeFees(marketId);
            
            address analytics = IAdminManager(adminManager).analytics();
            if (analytics != address(0)) {
                MetricsHub(analytics).trackMarketResolution(marketId, _winningOption);
            }
            
            emit MarketResolved(marketId, _winningOption);
        } else {
            revert("EventPool: Insufficient verifier votes for this option");
        }
    }

    /**
     * @dev Auto-resolve market when verifier quorum is reached
     */
    function autoResolve(uint256 marketId) external {
        Market storage market = markets[marketId];
        require(market.state == MarketState.Ended, "EventPool: Market not in ended state");
        require(!market.isResolved, "EventPool: Already resolved");
        
        address verification = IAdminManager(adminManager).verification();
        (bool resolved, uint256 votedOption) = ValidationCore(verification).isResolved(marketId);
        require(resolved, "EventPool: No resolution reached");
        
        market.winningOption = votedOption;
        market.isResolved = true;
        market.state = MarketState.Resolved;
        
        _trackResolvingVerifiers(marketId, votedOption);
        _distributeFees(marketId);
        
        address analytics = IAdminManager(adminManager).analytics();
        if (analytics != address(0)) {
            MetricsHub(analytics).trackMarketResolution(marketId, votedOption);
        }
        
        emit MarketResolved(marketId, votedOption);
    }

    /**
     * @dev Cancel market (anyone can call after resolution period)
     */
    function cancelMarket(uint256 marketId) external {
        Market storage market = markets[marketId];
        require(market.state == MarketState.Ended, "EventPool: Market not in ended state");
        require(block.timestamp >= market.resolutionEndTime, "EventPool: Resolution period not ended");
        require(!market.isResolved, "EventPool: Already resolved");
        
        _cancelMarket(marketId, "Resolution period expired");
    }

    /**
     * @dev Creator can cancel market (up to 12 hours before end)
     */
    function creatorCancelMarket(uint256 marketId) external {
        Market storage market = markets[marketId];
        require(msg.sender == market.creator, "EventPool: Only creator can cancel");
        require(market.state == MarketState.Active, "EventPool: Market not active");
        require(block.timestamp < market.stakeEndTime - 12 hours, "EventPool: Too late to cancel");
        
        _cancelMarket(marketId, "Creator cancelled");
    }

    /**
     * @dev Claim refunds for cancelled market
     */
    function claimRefund(uint256 marketId) external {
        Market storage market = markets[marketId];
        require(market.state == MarketState.Cancelled, "EventPool: Market not cancelled");
        require(userHasStaked[marketId][msg.sender] || userHasSupported[marketId][msg.sender], "EventPool: No stake to refund");
        
        uint256 refundAmount = 0;
        
        if (userHasStaked[marketId][msg.sender]) {
            uint256 stakeAmount = PoolVault(treasury).getUserStake(marketId, msg.sender, market.paymentToken);
            refundAmount += stakeAmount;
            userHasStaked[marketId][msg.sender] = false;
        }
        
        if (userHasSupported[marketId][msg.sender]) {
            uint256 supportAmount = PoolVault(treasury).getUserSupport(marketId, msg.sender, market.paymentToken);
            refundAmount += supportAmount;
            userHasSupported[marketId][msg.sender] = false;
        }
        
        require(refundAmount > 0, "EventPool: No refund available");
        
        PoolVault(treasury).claimRefund(marketId, msg.sender, market.paymentToken, refundAmount);
        emit RefundClaimed(marketId, msg.sender, refundAmount);
    }

    /**
     * @dev Calculate winnings for a user without claiming
     */
    function calculateWinnings(uint256 marketId, address user) external view returns (uint256) {
        Market storage market = markets[marketId];
        require(market.state == MarketState.Resolved, "EventPool: Market not resolved");
        require(market.winningOption > 0, "EventPool: No winning option");
        require(userHasStaked[marketId][user], "EventPool: No stake placed");
        require(!PoolVault(treasury).hasUserClaimed(marketId, user), "EventPool: Already claimed");
        
        uint256 userStake = PoolVault(treasury).getUserStake(marketId, user, market.paymentToken);
        uint256 totalWinningStake = PoolVault(treasury).getOptionPool(marketId, market.winningOption, market.paymentToken);
        
        uint256 totalLosingPool = 0;
        for (uint256 i = 1; i <= market.maxOptions; i++) {
            if (i != market.winningOption) {
                totalLosingPool += PoolVault(treasury).getOptionPool(marketId, i, market.paymentToken);
            }
        }
        
        uint256 supportPool = PoolVault(treasury).getSupportPool(marketId, market.paymentToken);
        totalLosingPool += supportPool;
        
        uint256 userWinnings;
        
        if (totalWinningStake == 0) {
            userWinnings = 0;
        } else if (totalLosingPool == 0) {
            bool isWinningStake = userStakeOptions[marketId][user] == market.winningOption;
            
            if (isWinningStake) {
                userWinnings = userStake;
            } else {
                userWinnings = 0;
            }
        } else {
            bool isWinningStake = userStakeOptions[marketId][user] == market.winningOption;
            
            if (isWinningStake) {
                uint256 winningsPool = (totalLosingPool * 9000) / 10000;
                userWinnings = userStake + (userStake * winningsPool) / totalWinningStake;
            } else {
                userWinnings = 0;
            }
        }
        
        return userWinnings;
    }

    /**
     * @dev Claim winnings for resolved market
     */
    function claimWinnings(uint256 marketId) external {
        Market storage market = markets[marketId];
        require(market.state == MarketState.Resolved, "EventPool: Market not resolved");
        require(market.winningOption > 0, "EventPool: No winning option");
        require(userHasStaked[marketId][msg.sender], "EventPool: No stake placed");
        require(!PoolVault(treasury).hasUserClaimed(marketId, msg.sender), "EventPool: Already claimed");
        
        uint256 userStake = PoolVault(treasury).getUserStake(marketId, msg.sender, market.paymentToken);
        uint256 totalWinningStake = PoolVault(treasury).getOptionPool(marketId, market.winningOption, market.paymentToken);
        
        uint256 totalLosingPool = 0;
        for (uint256 i = 1; i <= market.maxOptions; i++) {
            if (i != market.winningOption) {
                totalLosingPool += PoolVault(treasury).getOptionPool(marketId, i, market.paymentToken);
            }
        }
        
        uint256 supportPool = PoolVault(treasury).getSupportPool(marketId, market.paymentToken);
        totalLosingPool += supportPool;
        
        uint256 userWinnings;
        
        if (totalWinningStake == 0) {
            userWinnings = 0;
        } else if (totalLosingPool == 0) {
            bool isWinningStake = userStakeOptions[marketId][msg.sender] == market.winningOption;
            
            if (isWinningStake) {
                userWinnings = userStake;
            } else {
                userWinnings = 0;
            }
        } else {
            bool isWinningStake = userStakeOptions[marketId][msg.sender] == market.winningOption;
            
            if (isWinningStake) {
                uint256 winningsPool = (totalLosingPool * 9000) / 10000;
                userWinnings = userStake + (userStake * winningsPool) / totalWinningStake;
            } else {
                userWinnings = 0;
            }
        }
        
        PoolVault(treasury).claimWinnings(marketId, msg.sender, market.paymentToken, userWinnings);
        
        emit WinningsClaimed(marketId, msg.sender, userWinnings);
    }

    /**
     * @dev Internal function to cancel market
     */
    function _cancelMarket(uint256 marketId, string memory reason) internal {
        markets[marketId].state = MarketState.Cancelled;
        emit MarketCancelled(marketId, reason);
    }

    /**
     * @dev Internal function to track which verifiers resolved a market
     */
    function _trackResolvingVerifiers(uint256 marketId, uint256 winningOption) internal {
        address verification = IAdminManager(adminManager).verification();
        address[] memory allVerifiers = ValidationCore(verification).getVerifiers();
        
        for (uint256 i = 0; i < allVerifiers.length; i++) {
            if (ValidationCore(verification).hasVerifierVoted(marketId, winningOption, allVerifiers[i])) {
                marketResolvingVerifiers[marketId].push(allVerifiers[i]);
            }
        }
    }

    /**
     * @dev Internal function to distribute platform fees to treasury
     */
    function _distributeFees(uint256 marketId) internal {
        Market storage market = markets[marketId];
        
        uint256 totalLosingPool = 0;
        for (uint256 i = 1; i <= market.maxOptions; i++) {
            if (i != market.winningOption) {
                totalLosingPool += PoolVault(treasury).getOptionPool(marketId, i, market.paymentToken);
            }
        }
        
        uint256 supportPool = PoolVault(treasury).getSupportPool(marketId, market.paymentToken);
        totalLosingPool += supportPool;
        
        uint256 totalWinningStake = PoolVault(treasury).getOptionPool(marketId, market.winningOption, market.paymentToken);
        uint256 totalPool = PoolVault(treasury).getMarketPool(marketId, market.paymentToken);
        
        uint256 creatorFee = (totalPool * CREATOR_FEE_BPS) / 10000;
        uint256 verifierFee = (totalPool * VERIFIER_FEE_BPS) / 10000;
        uint256 platformFee = (totalPool * PLATFORM_FEE_BPS) / 10000;
        
        if (totalWinningStake == 0) {
            // All lose scenario - Creator gets stake back, platform gets the rest
            uint256 totalFees = creatorFee + verifierFee + platformFee;
            uint256 remainingForPlatform = totalPool - totalFees;
            
            if (creatorFee > 0) {
                PoolVault(treasury).transferToCreator(market.creator, market.paymentToken, creatorFee);
            }
            
            if (verifierFee > 0) {
                PoolVault(treasury).distributeVerifierRewards(marketId, market.paymentToken, verifierFee);
            }
            
            uint256 totalPlatformAmount = platformFee + remainingForPlatform;
            if (totalPlatformAmount > 0) {
                PoolVault(treasury).addToPlatformPool(market.paymentToken, totalPlatformAmount);
            }
        } else if (totalLosingPool == 0) {
            // All win scenario - No fees, no verifier rewards
            // Everyone gets their stakes back completely
        } else {
            // Normal scenario - Fees from losing pool
            if (creatorFee > 0) {
                PoolVault(treasury).transferToCreator(market.creator, market.paymentToken, creatorFee);
            }
            
            if (verifierFee > 0) {
                PoolVault(treasury).distributeVerifierRewards(marketId, market.paymentToken, verifierFee);
            }
            
            if (platformFee > 0) {
                PoolVault(treasury).distributeFees(market.paymentToken, platformFee);
            }
        }
    }

    /**
     * @dev Internal function to remove market from active list
     */
    function _removeFromActiveMarkets(uint256 marketId) internal {
        for (uint256 i = 0; i < activeMarkets.length; i++) {
            if (activeMarkets[i] == marketId) {
                activeMarkets[i] = activeMarkets[activeMarkets.length - 1];
                activeMarkets.pop();
                isActiveMarket[marketId] = false;
                break;
            }
        }
    }

    // ============ ADMIN FUNCTIONS (DELEGATED TO ADMINMANAGER) ============
    
    /**
     * @dev Update AdminManager address (one-time or emergency)
     */
    function setAdminManager(address _adminManager) external onlyOwner {
        require(_adminManager != address(0), "EventPool: Invalid address");
        adminManager = _adminManager;
    }
    
    /**
     * @dev Mark market as deleted (only callable by AdminManager)
     */
    function markMarketDeleted(uint256 marketId) external {
        require(msg.sender == adminManager, "EventPool: Only AdminManager");
        require(marketId < nextMarketId, "EventPool: Invalid market ID");
        require(markets[marketId].creator != address(0), "EventPool: Market does not exist");
        require(markets[marketId].state != MarketState.Deleted, "EventPool: Already deleted");
        
        markets[marketId].state = MarketState.Deleted;
        
        // Remove from active markets list
        if (isActiveMarket[marketId]) {
            _removeFromActiveMarkets(marketId);
        }
        
        emit MarketCancelled(marketId, "Market deleted by admin");
    }
    
    /**
     * @dev Mark market as permanently removed (only callable by AdminManager)
     */
    function markMarketPermanentlyRemoved(uint256 marketId) external {
        require(msg.sender == adminManager, "EventPool: Only AdminManager");
        require(marketId < nextMarketId, "EventPool: Invalid market ID");
        require(markets[marketId].creator != address(0), "EventPool: Market does not exist");
        require(markets[marketId].state == MarketState.Resolved, "EventPool: Market not resolved");
        
        // Clear market data
        delete markets[marketId];
        
        // Remove from active markets if still there
        if (isActiveMarket[marketId]) {
            _removeFromActiveMarkets(marketId);
        }
        
        emit MarketCancelled(marketId, "Permanently removed after claim period");
    }

    // ============ VIEW FUNCTIONS ============
    
    function getMarket(uint256 marketId) external view returns (Market memory) {
        return markets[marketId];
    }

    function getUserStake(uint256 marketId, address user, address token) external view returns (uint256) {
        return PoolVault(treasury).getUserStake(marketId, user, token);
    }

    function getOptionPool(uint256 marketId, uint256 option, address token) external view returns (uint256) {
        return PoolVault(treasury).getOptionPool(marketId, option, token);
    }

    function getTotalPool(uint256 marketId, address token) external view returns (uint256) {
        return PoolVault(treasury).getMarketPool(marketId, token);
    }

    function getSupportPool(uint256 marketId, address token) external view returns (uint256) {
        return PoolVault(treasury).getSupportPool(marketId, token);
    }

    function getUserSupport(uint256 marketId, address user, address token) external view returns (uint256) {
        return PoolVault(treasury).getUserSupport(marketId, user, token);
    }

    function getSupporterCount(uint256 marketId) external view returns (uint256) {
        return marketSupporters[marketId].length;
    }

    function getStakerCount(uint256 marketId) external view returns (uint256) {
        return marketStakers[marketId].length;
    }

    function getMarketStakers(uint256 marketId) external view returns (address[] memory) {
        return marketStakers[marketId];
    }

    /**
     * @dev Get comprehensive market information
     */
    function getMarketInfo(uint256 marketId) external view returns (
        Market memory market,
        uint256 totalPool,
        uint256 supportPool,
        uint256 stakerCount,
        uint256 supporterCount,
        address[] memory stakers,
        address[] memory supporters,
        string memory tokenSymbol
    ) {
        market = markets[marketId];
        totalPool = PoolVault(treasury).getMarketPool(marketId, market.paymentToken);
        supportPool = PoolVault(treasury).getSupportPool(marketId, market.paymentToken);
        stakerCount = marketStakers[marketId].length;
        supporterCount = marketSupporters[marketId].length;
        stakers = marketStakers[marketId];
        supporters = marketSupporters[marketId];
        tokenSymbol = IAdminManager(adminManager).tokenSymbols(market.paymentToken);
    }

    function getNextMarketId() external view returns (uint256) {
        return nextMarketId;
    }
    
    function getActiveMarkets() external view returns (uint256[] memory) {
        return activeMarkets;
    }
    
    function getUserMarketHistory(address user) external view returns (uint256[] memory) {
        return userMarketHistory[user];
    }
    
    function isMarketActive(uint256 marketId) external view returns (bool) {
        return isActiveMarket[marketId] && markets[marketId].state != MarketState.Deleted;
    }
    
    function getActiveMarketCount() external view returns (uint256) {
        return activeMarkets.length;
    }
    
    function getAllMarketIds() external view returns (uint256[] memory) {
        uint256[] memory allIds = new uint256[](nextMarketId - 1);
        for (uint256 i = 1; i < nextMarketId; i++) {
            allIds[i - 1] = i;
        }
        return allIds;
    }

    function getMarketResolvingVerifiers(uint256 marketId) external view returns (address[] memory) {
        return marketResolvingVerifiers[marketId];
    }

    receive() external payable {}
}

contracts/P2PToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract PoolToken is ERC20, Ownable {
    constructor(
        address initialOwner,
        uint256 initialSupply
    ) ERC20("P2P Token", "P2P") Ownable(initialOwner) {
        _mint(initialOwner, initialSupply * 10 ** decimals());
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    function burn(uint256 amount) public {
        _burn(msg.sender, amount);
    }
}

contracts/P2PTreasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

// ✅ COPY THIS FILE - Fixed PoolVault with platform pool tracking and proper partner allocation

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// Interface for MarketManager
interface IMarketManager {
    function getMarketResolvingVerifiers(uint256 marketId) external view returns (address[] memory);
}

contract PoolVault is Ownable {
    using SafeERC20 for IERC20;

    // Platform fee percentage (5% = 500 basis points)
    uint256 public constant PLATFORM_FEE_BPS = 500;
    
    // Partner fee percentage (20% of platform fee = 1% of total = 100 basis points)
    uint256 public constant PARTNER_FEE_BPS = 100;
    
    // Partner address
    address public partner;
    
    // Authorized contracts that can distribute fees
    mapping(address => bool) public authorizedContracts;
    
    // MarketManager address (can be changed)
    address public marketManager;
    
    // Platform pool tracking - separate for each token
    mapping(address => uint256) public platformPools; // token => platform pool amount
    
    // Market fund tracking
    mapping(uint256 => mapping(address => uint256)) public marketPools; // marketId => token => total amount
    mapping(uint256 => mapping(uint256 => mapping(address => uint256))) public optionPools; // marketId => option => token => amount
    mapping(uint256 => mapping(address => uint256)) public supportPools; // marketId => token => support amount
    mapping(uint256 => mapping(address => mapping(address => uint256))) public userStakes; // marketId => user => token => stake amount
    mapping(uint256 => mapping(address => mapping(address => uint256))) public userSupports; // marketId => user => token => support amount
    mapping(uint256 => mapping(address => mapping(uint256 => uint256))) public userBetOptions; // marketId => user => option => bet amount
    mapping(uint256 => mapping(address => bool)) public userClaimed; // marketId => user => has claimed
    mapping(uint256 => mapping(address => bool)) public userSupportClaimed; // marketId => user => has claimed support
    
    // Events
    event PartnerUpdated(address indexed oldPartner, address indexed newPartner);
    event FeesDistributed(address indexed token, uint256 platformAmount, uint256 partnerAmount);
    event PlatformPoolUpdated(address indexed token, uint256 amount, string reason);
    event PlatformFundsWithdrawn(address indexed token, uint256 amount, address indexed to);
    event FundsWithdrawn(address indexed token, uint256 amount, address indexed to);
    event ContractAuthorized(address indexed contractAddr);
    event ContractDeauthorized(address indexed contractAddr);
    event MarketManagerUpdated(address indexed oldManager, address indexed newManager);

    constructor(address initialOwner, address _partner) Ownable(initialOwner) {
        partner = _partner;
    }

    /**
     * @dev Update partner address
     */
    function setPartner(address _partner) external onlyOwner {
        require(_partner != address(0), "Treasury: Invalid partner address");
        address oldPartner = partner;
        partner = _partner;
        emit PartnerUpdated(oldPartner, _partner);
    }

    /**
     * @dev Calculate platform and partner fees from total amount
     */
    function calculateFees(uint256 totalAmount) public pure returns (uint256 platformFee, uint256 partnerFee) {
        platformFee = (totalAmount * PLATFORM_FEE_BPS) / 10000;
        partnerFee = (platformFee * PARTNER_FEE_BPS) / 10000;
    }

    /**
     * @dev Authorize a contract to distribute fees
     */
    function authorizeContract(address contractAddr) external onlyOwner {
        require(contractAddr != address(0), "Treasury: Invalid contract address");
        authorizedContracts[contractAddr] = true;
        emit ContractAuthorized(contractAddr);
    }

    /**
     * @dev Deauthorize a contract from distributing fees
     */
    function deauthorizeContract(address contractAddr) external onlyOwner {
        authorizedContracts[contractAddr] = false;
        emit ContractDeauthorized(contractAddr);
    }

    /**
     * @dev Set MarketManager address
     */
    function setMarketManager(address _marketManager) external onlyOwner {
        require(_marketManager != address(0), "Treasury: Invalid market manager address");
        address oldManager = marketManager;
        marketManager = _marketManager;
        
        // Auto-authorize the new market manager
        authorizedContracts[_marketManager] = true;
        
        emit MarketManagerUpdated(oldManager, _marketManager);
        emit ContractAuthorized(_marketManager);
    }

    /**
     * @dev Distribute fees to platform and partner (for normal fee distribution)
     * @param token ERC20 token address (address(0) for native ETH)
     * @param totalAmount Total amount to distribute fees from
     */
    function distributeFees(address token, uint256 totalAmount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can distribute fees");
        require(totalAmount > 0, "Treasury: Invalid amount");
        
        (uint256 platformFee, uint256 partnerFee) = calculateFees(totalAmount);
        
        if (token == address(0)) {
            // Native ETH
            require(address(this).balance >= totalAmount, "Treasury: Insufficient ETH balance");
            
            if (partnerFee > 0) {
                (bool success, ) = partner.call{value: partnerFee}("");
                require(success, "Treasury: ETH transfer to partner failed");
            }
            
            // Add platform fee to platform pool
            platformPools[token] += (platformFee - partnerFee);
            emit PlatformPoolUpdated(token, (platformFee - partnerFee), "Regular fee distribution");
            
            emit FeesDistributed(token, platformFee, partnerFee);
        } else {
            // ERC20 token
            IERC20 tokenContract = IERC20(token);
            require(tokenContract.balanceOf(address(this)) >= totalAmount, "Treasury: Insufficient token balance");
            
            if (partnerFee > 0) {
                tokenContract.safeTransfer(partner, partnerFee);
            }
            
            // Add platform fee to platform pool
            platformPools[token] += (platformFee - partnerFee);
            emit PlatformPoolUpdated(token, (platformFee - partnerFee), "Regular fee distribution");
            
            emit FeesDistributed(token, platformFee, partnerFee);
        }
    }

    /**
     * @dev Add funds directly to platform pool (for "everyone loses" scenarios)
     * @param token ERC20 token address (address(0) for native ETH)
     * @param amount Amount to add to platform pool
     */
    function addToPlatformPool(address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can add to platform pool");
        require(amount > 0, "Treasury: Invalid amount");
        
        // Partner gets 20% of the platform amount (same as normal fee distribution)
        uint256 partnerFee = (amount * 2000) / 10000; // 20% of platform amount
        uint256 platformAmount = amount - partnerFee;
        
        if (token == address(0)) {
            // Native ETH
            require(address(this).balance >= amount, "Treasury: Insufficient ETH balance");
            
            if (partnerFee > 0) {
                (bool success, ) = partner.call{value: partnerFee}("");
                require(success, "Treasury: ETH transfer to partner failed");
            }
        } else {
            // ERC20 token
            IERC20 tokenContract = IERC20(token);
            require(tokenContract.balanceOf(address(this)) >= amount, "Treasury: Insufficient token balance");
            
            if (partnerFee > 0) {
                tokenContract.safeTransfer(partner, partnerFee);
            }
        }
        
        // Add remaining amount to platform pool
        platformPools[token] += platformAmount;
        
        emit PlatformPoolUpdated(token, platformAmount, "Direct platform pool addition");
        emit FeesDistributed(token, platformAmount, partnerFee);
    }

    /**
     * @dev Get platform pool balance for a specific token
     * @param token ERC20 token address (address(0) for native ETH)
     */
    function getPlatformPool(address token) external view returns (uint256) {
        return platformPools[token];
    }

    /**
     * @dev Withdraw platform funds (only owner)
     * @param token ERC20 token address (address(0) for native ETH)
     * @param amount Amount to withdraw
     * @param to Recipient address
     */
    function withdrawPlatformFunds(address token, uint256 amount, address to) external onlyOwner {
        require(to != address(0), "Treasury: Invalid recipient");
        require(amount > 0, "Treasury: Invalid amount");
        require(platformPools[token] >= amount, "Treasury: Insufficient platform pool balance");
        
        // Reduce platform pool balance
        platformPools[token] -= amount;
        
        if (token == address(0)) {
            require(address(this).balance >= amount, "Treasury: Insufficient ETH balance");
            (bool success, ) = to.call{value: amount}("");
            require(success, "Treasury: ETH transfer failed");
        } else {
            IERC20 tokenContract = IERC20(token);
            require(tokenContract.balanceOf(address(this)) >= amount, "Treasury: Insufficient token balance");
            tokenContract.safeTransfer(to, amount);
        }
        
        emit PlatformFundsWithdrawn(token, amount, to);
        emit PlatformPoolUpdated(token, platformPools[token], "Platform funds withdrawal");
    }

    /**
     * @dev Emergency withdraw funds (only owner) - for non-platform funds
     */
    function withdraw(address token, uint256 amount, address to) external onlyOwner {
        require(to != address(0), "Treasury: Invalid recipient");
        require(amount > 0, "Treasury: Invalid amount");
        
        if (token == address(0)) {
            require(address(this).balance >= amount, "Treasury: Insufficient ETH balance");
            (bool success, ) = to.call{value: amount}("");
            require(success, "Treasury: ETH transfer failed");
        } else {
            IERC20 tokenContract = IERC20(token);
            require(tokenContract.balanceOf(address(this)) >= amount, "Treasury: Insufficient token balance");
            tokenContract.safeTransfer(to, amount);
        }
        
        emit FundsWithdrawn(token, amount, to);
    }

    /**
     * @dev Place a stake (called by MarketManager)
     */
    function placeStake(uint256 marketId, address user, address token, uint256 amount, uint256 option) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can place stakes");
        require(amount > 0, "Treasury: Invalid stake amount");
        
        // Update user stake
        userStakes[marketId][user][token] += amount;
        userBetOptions[marketId][user][option] += amount;
        
        // Update pools
        marketPools[marketId][token] += amount;
        optionPools[marketId][option][token] += amount;
    }

    /**
     * @dev Support a market (called by MarketManager)
     */
    function supportMarket(uint256 marketId, address user, address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can support markets");
        require(amount > 0, "Treasury: Invalid support amount");
        
        // Update user support
        userSupports[marketId][user][token] += amount;
        
        // Update support pool
        supportPools[marketId][token] += amount;
    }

    /**
     * @dev Withdraw support (called by MarketManager)
     */
    function withdrawSupport(uint256 marketId, address user, address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can withdraw support");
        require(amount > 0, "Treasury: Invalid withdrawal amount");
        require(userSupports[marketId][user][token] >= amount, "Treasury: Insufficient support balance");
        
        // Update user support
        userSupports[marketId][user][token] -= amount;
        
        // Update support pool
        supportPools[marketId][token] -= amount;
        
        // Transfer funds
        _transferPayment(user, amount, token);
    }

    /**
     * @dev Claim winnings (called by MarketManager)
     */
    function claimWinnings(uint256 marketId, address user, address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can claim winnings");
        require(amount > 0, "Treasury: Invalid winnings amount");
        require(!userClaimed[marketId][user], "Treasury: Already claimed");
        
        userClaimed[marketId][user] = true;
        
        // Transfer funds
        _transferPayment(user, amount, token);
    }

    /**
     * @dev Claim refund (called by MarketManager)
     */
    function claimRefund(uint256 marketId, address user, address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can claim refunds");
        require(amount > 0, "Treasury: Invalid refund amount");
        
        // Transfer funds
        _transferPayment(user, amount, token);
    }

    /**
     * @dev Internal function to transfer payment
     */
    function _transferPayment(address to, uint256 amount, address token) internal {
        if (token == address(0)) {
            (bool success, ) = to.call{value: amount}("");
            require(success, "Treasury: ETH transfer failed");
        } else {
            IERC20(token).safeTransfer(to, amount);
        }
    }

    /**
     * @dev Get user bet amount
     */
    function getUserStake(uint256 marketId, address user, address token) external view returns (uint256) {
        return userStakes[marketId][user][token];
    }

    /**
     * @dev Get user support amount
     */
    function getUserSupport(uint256 marketId, address user, address token) external view returns (uint256) {
        return userSupports[marketId][user][token];
    }

    /**
     * @dev Get option pool amount
     */
    function getOptionPool(uint256 marketId, uint256 option, address token) external view returns (uint256) {
        return optionPools[marketId][option][token];
    }

    /**
     * @dev Get support pool amount
     */
    function getSupportPool(uint256 marketId, address token) external view returns (uint256) {
        return supportPools[marketId][token];
    }

    /**
     * @dev Get total market pool (betting pools + support pools)
     */
    function getMarketPool(uint256 marketId, address token) external view returns (uint256) {
        uint256 bettingPool = marketPools[marketId][token];
        uint256 supportPool = supportPools[marketId][token];
        return bettingPool + supportPool;
    }

    /**
     * @dev Check if user has claimed
     */
    function hasUserClaimed(uint256 marketId, address user) external view returns (bool) {
        return userClaimed[marketId][user];
    }

    /**
     * @dev Check if user has claimed support
     */
    function hasUserSupportClaimed(uint256 marketId, address user) external view returns (bool) {
        return userSupportClaimed[marketId][user];
    }

    /**
     * @dev Get all platform pools info (for UI/monitoring)
     */
    function getAllPlatformPools(address[] calldata tokens) external view returns (uint256[] memory balances) {
        balances = new uint256[](tokens.length);
        for (uint256 i = 0; i < tokens.length; i++) {
            balances[i] = platformPools[tokens[i]];
        }
    }

    /**
     * @dev Transfer creator's platform fee directly to creator
     */
    function transferToCreator(address creator, address token, uint256 amount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can transfer to creator");
        require(amount > 0, "Treasury: Invalid amount");
        require(creator != address(0), "Treasury: Invalid creator address");
        
        if (token == address(0)) {
            // Native ETH
            require(address(this).balance >= amount, "Treasury: Insufficient ETH balance");
            (bool success, ) = creator.call{value: amount}("");
            require(success, "Treasury: ETH transfer to creator failed");
        } else {
            // ERC20 token
            require(IERC20(token).balanceOf(address(this)) >= amount, "Treasury: Insufficient token balance");
            IERC20(token).safeTransfer(creator, amount);
        }
        
        emit FundsWithdrawn(token, amount, creator);
    }

    /**
     * @dev Distribute verifier rewards to verifiers who resolved a market
     * Each verifier gets 0.5% of total pool (1.5% total split among 3 verifiers)
     */
    function distributeVerifierRewards(uint256 marketId, address token, uint256 totalAmount) external {
        require(authorizedContracts[msg.sender], "Treasury: Only authorized contracts can distribute verifier rewards");
        require(totalAmount > 0, "Treasury: Invalid reward amount");
        
        // Get resolving verifiers from MarketManager
        address[] memory resolvingVerifiers = IMarketManager(msg.sender).getMarketResolvingVerifiers(marketId);
        require(resolvingVerifiers.length > 0, "Treasury: No resolving verifiers found");
        
        // Calculate reward per verifier (0.5% each)
        uint256 rewardPerVerifier = totalAmount / resolvingVerifiers.length;
        
        // Distribute rewards to each verifier
        for (uint256 i = 0; i < resolvingVerifiers.length; i++) {
            if (rewardPerVerifier > 0) {
                _transferPayment(resolvingVerifiers[i], rewardPerVerifier, token);
            }
        }
        
        // Handle any remainder (due to division)
        uint256 totalDistributed = rewardPerVerifier * resolvingVerifiers.length;
        uint256 remainder = totalAmount - totalDistributed;
        
        if (remainder > 0) {
            // Give remainder to first verifier
            _transferPayment(resolvingVerifiers[0], remainder, token);
        }
        
        emit FundsWithdrawn(token, totalAmount, address(0)); // Use address(0) to indicate verifier distribution
    }

    /**
     * @dev Get contract balance vs platform pool balance (for debugging)
     */
    function getBalanceInfo(address token) external view returns (uint256 contractBalance, uint256 platformPoolBalance) {
        platformPoolBalance = platformPools[token];
        
        if (token == address(0)) {
            contractBalance = address(this).balance;
        } else {
            contractBalance = IERC20(token).balanceOf(address(this));
        }
    }

    /**
     * @dev Receive ETH
     */
    receive() external payable {}
}

contracts/P2PVerification.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

// Interface for EventPool auto-resolution
interface IEventPool {
    function autoResolve(uint256 marketId) external;
}

contract ValidationCore is Ownable {
    // Maximum number of verifiers
    uint256 public constant MAX_VERIFIERS = 5;
    
    // Required quorum for resolution
    uint256 public constant REQUIRED_QUORUM = 3;
    
    // Verifier addresses
    address[] public verifiers;
    
    // Mapping to check if address is a verifier
    mapping(address => bool) public isVerifier;
    
    // MarketManager address (for auto-resolution)
    address public marketManager;
    
    // Market resolution votes
    mapping(uint256 => mapping(uint256 => bool)) public hasVoted; // marketId => option => voted
    mapping(uint256 => mapping(uint256 => uint256)) public voteCount; // marketId => option => count
    mapping(uint256 => mapping(uint256 => mapping(address => bool))) public verifierVoted; // marketId => option => verifier => voted
    
    // Events
    event VerifierAdded(address indexed verifier);
    event VerifierRemoved(address indexed verifier);
    event VoteCast(address indexed market, uint256 option, address indexed verifier);
    event ResolutionReached(address indexed market, uint256 winningOption);
    event MarketManagerUpdated(address indexed oldManager, address indexed newManager);
    event AutoResolutionTriggered(uint256 indexed marketId, uint256 winningOption);

    constructor(address initialOwner) Ownable(initialOwner) {}

    /**
     * @dev Set MarketManager address
     */
    function setMarketManager(address _marketManager) external onlyOwner {
        require(_marketManager != address(0), "Verification: Invalid market manager address");
        address oldManager = marketManager;
        marketManager = _marketManager;
        emit MarketManagerUpdated(oldManager, _marketManager);
    }

    /**
     * @dev Add a new verifier
     */
    function addVerifier(address verifier) external onlyOwner {
        require(verifier != address(0), "Verification: Invalid verifier address");
        require(!isVerifier[verifier], "Verification: Already a verifier");
        require(verifiers.length < MAX_VERIFIERS, "Verification: Maximum verifiers reached");
        
        verifiers.push(verifier);
        isVerifier[verifier] = true;
        
        emit VerifierAdded(verifier);
    }

    /**
     * @dev Remove a verifier
     */
    function removeVerifier(address verifier) external onlyOwner {
        require(isVerifier[verifier], "Verification: Not a verifier");
        
        // Find and remove verifier
        for (uint256 i = 0; i < verifiers.length; i++) {
            if (verifiers[i] == verifier) {
                verifiers[i] = verifiers[verifiers.length - 1];
                verifiers.pop();
                break;
            }
        }
        
        isVerifier[verifier] = false;
        
        emit VerifierRemoved(verifier);
    }

    /**
     * @dev Get all verifiers
     */
    function getVerifiers() external view returns (address[] memory) {
        return verifiers;
    }

    /**
     * @dev Get verifier count
     */
    function getVerifierCount() external view returns (uint256) {
        return verifiers.length;
    }

    /**
     * @dev Cast a vote for market resolution
     */
    function castVote(uint256 marketId, uint256 option) external {
        require(isVerifier[msg.sender], "Verification: Not a verifier");
        require(option > 0, "Verification: Invalid option");
        require(!verifierVoted[marketId][option][msg.sender], "Verification: Already voted for this option");
        
        // Mark this verifier as voted for this option
        verifierVoted[marketId][option][msg.sender] = true;
        
        // Increment vote count for this option
        voteCount[marketId][option]++;
        
        emit VoteCast(address(0), option, msg.sender); // Keep event compatible
        
        // Check if quorum reached
        if (voteCount[marketId][option] >= REQUIRED_QUORUM) {
            emit ResolutionReached(address(0), option);
            
            // Auto-resolve market if P2PMarketManager is set
            if (marketManager != address(0)) {
                try IEventPool(marketManager).autoResolve(marketId) {
                    emit AutoResolutionTriggered(marketId, option);
                } catch {
                    // P2PMarketManager might not be ready or market might not be in correct state
                    // This is not critical - manual resolution can still be called
                }
            }
        }
    }

    /**
     * @dev Check if market has reached resolution
     */
    function isResolved(uint256 marketId) external view returns (bool, uint256) {
        for (uint256 option = 1; option <= 10; option++) {
            if (voteCount[marketId][option] >= REQUIRED_QUORUM) {
                return (true, option);
            }
        }
        return (false, 0);
    }

    /**
     * @dev Get vote count for specific option
     */
    function getVoteCount(uint256 marketId, uint256 option) external view returns (uint256) {
        return voteCount[marketId][option];
    }

    /**
     * @dev Check if verifier has voted for specific option
     */
    function hasVerifierVoted(uint256 marketId, uint256 option, address verifier) external view returns (bool) {
        return verifierVoted[marketId][option][verifier];
    }

    /**
     * @dev Reset votes for a market (for testing or emergency)
     */
    function resetMarketVotes(uint256 marketId) external onlyOwner {
        for (uint256 option = 1; option <= 10; option++) {
            voteCount[marketId][option] = 0;
            for (uint256 i = 0; i < verifiers.length; i++) {
                verifierVoted[marketId][option][verifiers[i]] = false;
            }
        }
    }
}

contracts/PENKToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract PENKToken is ERC20, Ownable {
    constructor(
        address initialOwner,
        uint256 initialSupply
    ) ERC20("PENK Token", "PENK") Ownable(initialOwner) {
        _mint(initialOwner, initialSupply * 10 ** decimals());
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    function burn(uint256 amount) public {
        _burn(msg.sender, amount);
    }
}

